import cookie from 'react-cookies'
import 'whatwg-fetch'
import React, { Component,useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import PostInline from './compenents/post'
import Contact from './compenents/contact'
import {BrowserRouter,Route,Redirect,Switch} from 'react-router-dom'
import Navbar from './compenents/Navbar';
import Loading from './compenents/loading';
import Announce_post from './compenents/announce_part';
import axios from 'axios';

class Announcements extends Component {
    constructor(props){
        super(props);
        this.componentDidMount = this.componentDidMount.bind(this)
    }
    state = {
      loading:true,
        posts:[],
      }
      componentDidMount()
      {
        this.setState({
          posts:[],
        })
        axios.get("/asdfasdf/")
        .then(booksList => {
            console.log(booksList.data)
         if(this.state.posts !== booksList.data)
         {
            this.setState({ posts: booksList.data,loading:false});
         }  
        });
      }
    render() { 
        if(this.state.loading){
          return <Loading />
        }
        else{
          return ( 
            <div>
                <Navbar/><br/>
                <div className="container">
                    <h3>Your Announcement</h3><br/>
                    <p>After Your product is sold,Please press "delete" button</p>
                    <div className='container'>
                        <div className='row'>
                        {this.state.posts.length > 0 ?
                           <React.Fragment>
                             <table class="table table-hover">
                                <tbody>
                                <tr>
                                    <td>Product Name</td>
                                    <td>Date</td>
                                    <td>Edit</td>
                                    <td>Delete</td>
                                </tr>
                                  {this.state.posts.map(post => <Announce_post key = {post.id} mount = {this.componentDidMount} post = {post} /> )}
                                </tbody>
                            </table>
                          </React.Fragment> : <div className='alert alert-danger'>You have never create any announcement</div>} 
        </div></div> 
                </div>
            </div>
         );
        }
    }
}
 
export default Announcements;
