import React, { Component } from 'react';
import Navbar from './compenents/Navbar'
import axios from 'axios'
import Detail1 from './compenents/detail1'

class Detail extends Component {
    constructor(props){
        super(props);
        this.state = {
            slug:null,
            array:[]
        }
    }
    componentDidMount()
    {   
        
        let comp = this;
        comp.setState({
            slug:null,
        })
        if(comp.props.match)
        {
           const slug = comp.props.match.params.slug
           comp.setState({
               slug:slug
           })
           let url = "/detail/" + slug;
           axios.get(url)
           .then(response=>{
            comp.setState({
                array:response.data
            }) 
            console.log(comp.state.array)
           })
           
           .catch(errors =>{
               console.log(errors)
           })
        }
        else{
            console.log("There is not any slug")
        }
    }
    componentDidUpdate(){
        let comp = this;
        comp.setState({
            slug:null,
            array:[]
        })
        setTimeout(() => {
            if(comp.props.match.params){
                const slug = comp.props.match.params.slug
                comp.setState({
                    slug:slug
                });
                let url = "/url/"+slug;
                axios.get(url)
                    .then(response=>{
                        console.log(response.data)
                        comp.setState({
                            array:response.data
                        })
                        
                    })
            }
           
        }, 1000);
    }
   
    render() {
        return (
        <div>
            <Navbar />
            <Detail1 pro={this.state.array}/> 
        </div>
        );
    }
}
 
export default Detail;
