import React, { Component } from 'react';

class CreateProduct extends Component {
    constructor(props){
        super(props);
        this.state = {
            name:null,
            color:null,
            desc:null,
            price:null,
            sale:false,
            price_in_sale:null,
            quantity:null,
            img:undefined,
            size:null,
            date:undefined,
            category:null,
            brand:null,
        }
    }
    handleForm(event) {
        event.preventDefault();
        console.log(event.target.name,event.target.value)
        this.setState({
            [event.target.name]:event.target.value,
        })
    }
    handleSubmit(event){
        event.preventDefault();
    }
    render() { 
        return ( 
            <div className="container text-blue">
                <form  onSubmit={this.handleSubmit()}>
                    <input type="text" name="name" className='form-control' onChange={this.handleForm()} required='required' />
                    <input type="number" className='form-control' name="quantiy" required='required' />
                    <input type="checkbox" className="form-control" name='sale' required='required' /
                    >
                </form>
            </div>
         );
    }
}
 
export default CreateProduct;
