export const getItemCyclic = <T>(index: number, array: Array<T>): T => {
  const id = index % array.length
  return array[id]
}
import { toIndex } from 'unit-fns'

export const getRandomItem = <T>(items: Array<T>, random = Math.random): T => {
  return items[toIndex(random(), items.length)]
}
export const offset = <T extends Array<any>>(list: T, amount: number): T => {
  return [...list.slice(amount), ...list.slice(0, amount)] as T
}
import { getItemCyclic } from './getItemCyclic'

export const patternChunks = <T>(array: Array<T>, pattern: Array<number>): Array<Array<T>> => {
  const result: Array<Array<T>> = []
  const _array = [...array]
  let i = 0

  while (_array.length > 0) {
    result.push(_array.splice(0, getItemCyclic(i, pattern)))
    i++
  }

  return result
}
export const replaceAt = <A>(
  array: Array<A>,
  index: number,
  replacementFn: (item: A) => A
): Array<A> => {
  const item = array[index]

  return array
    .slice(0, index)
    .concat([replacementFn(item)])
    .concat(array.slice(index + 1))
}
export const times = <T>(iterations: number, callback: (index: number) => T): Array<T> => {
  let result: Array<T> = []

  for (let i=0; i < iterations; i++) {
      result.push(callback(i));
  }

  return result
}
