import React, { Component } from 'react';

class PostInline extends Component {
    render() { 
        return (
        <div>
                    <div className="card" style="width:400px">
                        <img className="card-img-top" src="img_avatar1.png" />
                        <div className="card-body">
                        <h4 className="card-title">John Doe</h4>
                        <p className="card-text">Some example text some example text. John Doe is an architect and engineer</p>
                        <a href="#" className="btn btn-primary">See Profile</a>
                        </div>
                    </div>
                    <br/>
            <ul className="list-group">
                <li className="list-group-item active">Product Name: {this.props.post.name}</li>
                <li className="list-group-item">Product price: {this.props.post.price}$</li>
                <li className="list-group-item">Product Quantity in stock : {this.props.post.quantity} ta</li><br/><br/>
             </ul>
        </div> 
        );
    }
}
 
export default PostInline;
