import React, { Component } from 'react';
import Navbar from './compenents/Navbar'

class Detail extends Component {
    constructor(props){
        super(props);
        this.state = {
            slug:null
        }
    }
    componentDidMount()
    {
        let comp = this
        comp.setState({
            slug:null,
        })
        if(comp.props.match)
        {
           const {slug} = comp.props.match 
           comp.setState({
               slug:slug
           })
           console.log(slug)
        }
    }
    render() { 
        return (
        <div>
            <h1>Salom</h1>
            <Navbar />
            <div>{this.state.slug}</div>
        </div>
        );
    }
}
 
export default Detail;
