import axios from 'axios';
import React, { Component } from 'react';
import {Link} from 'react-router-dom'
class ProfileForm extends Component {
    state = {
        username:null,
        first_name:null,
        last_name:null,
        email:null,

    }
    componentDidMount(){
        axios.get('/rtqkaq2345akdker24/')
        .then(response =>{
            this.setState({
                username:response.data.username,
                first_name:response.data.first_name,
                last_name:response.data.last_name,
                email:response.data.email
            })
        })
        .catch(errors =>{
            console,log(errors)
        })
    }
    render() { 
        return ( 
            <form onSubmit={this.handlesubmit}>
                <label htmlFor="name">First Name</label>
                <input type="text" className="form-control" name="first_name" id="name" defaultValue={this.state.name} onChange = {this.handlefield} />
                <label htmlFor="surname">Surname</label>
                <input type="text" className="form-control" name="last_name" id="surname" defaultValue={this.state.surname} onChange={this.handlefield}  />
                <label htmlFor="username">Username</label>
                <input type="text" className="form-control" name="username" id="username" defaultValue={this.state.username} onChange={this.handlefield} />
                <label htmlFor="email">Your Email</label>
                <input type="email" className="form-control" name="email" id="email" defaultValue={this.state.email} onChange={this.handlefield} />
            </form>
         );
    }
}
 
export default ProfileForm;
