import cookie from 'react-cookies'
import 'whatwg-fetch'
import React, { Component,useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import PostInline from './compenents/post'
import Contact from './compenents/contact'
import {BrowserRouter,Route,Redirect,Switch} from 'react-router-dom'
import notfound from './404'
import axios from 'axios'
import Navbar from './compenents/Navbar';
class ContactPage extends Component {
  constructor(props){
    super(props);
    this.lang2 = this.lang2.bind(this)
  }
    state = {
      cart:0,
      wishlist:0,
      lang:''
    }
    contact = () =>{
        let csrf = cookie.load('csrftoken')
        if(csrf !== undefined && csrf !== null)
        {
          console.log("Exist");
          return (<React.Fragment><br/><h3>Contact</h3><Contact /></React.Fragment>);
        }
        else{
          <Redirect to='/notfound'/>
        }
        
      }
      componentDidMount(){
        axios.get('http://127.0.0.1:8000/sdlkewrws/')
        .then(response => {
            this.setState({
            cart:response.data.cart,
            wishlist:response.data.wishlist
            })
        })
      }
      lang2(ev){
        localStorage.setItem('lang',ev);
        this.setState({
            lang:ev
        })
    
      }
    render() { 
        return (
        <div>
          <Navbar state={this.state} lang2={this.lang2} />
            {this.contact()}
        </div>);
    }
}
 
export default ContactPage;
