import React, { Component } from 'react';
import cookie from 'react-cookies'
import axios from 'axios'
import Loading from './loading';
import {
  Link
} from "react-router-dom";
import {I18nProvider,LOCALES} from '../i18n'
import translate from '../i18n/tranlate'

class Navbar extends Component {
  constructor(props){
    super(props);
    this.user = this.user.bind(this)
    this.url2 = this.url2.bind(this)
  }
  state = {
    user:[],
    nothing:',',
    logout:false,
  }

  componentDidMount(){
    let cpomp = this;
    axios.get('http://127.0.0.1:8000/logggedin/')
      .then(response => {
        console.log(response)
        cpomp.setState({
          user:response.data
        })
      })
        .catch(errors =>{
          console.log("Request could not be sent")
          console.log(errors)
        })
  }
  user(){
    let cpomp = this;
    axios.get('http://127.0.0.1:8000/logout1/')
      .then(response => {
        cpomp.setState({
          user:response.data
        })
      })
        .catch(errors =>{
          console.log(errors)
        })  
  }


  authenticated = () => {
    if(this.state.user === 'False')
    {
      return (
      <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
            <li className="nav-item">
                <Link to="/login"><div className='nav-link'>Login</div></Link>
             </li> 
            <li className="nav-item">
                <Link to="/signup"><div className='nav-link'>Register</div></Link>
            </li> 
      </ul>)
    }
    else{
      return  (
      <div className='dropdown' style={{marginRight:'70px'}}>  
         <button className=" btn btn-light dropdown-toggle" href="#" id="navbarDropdown" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            <i className="fas fa-user"></i> {this.state.user["username"]} 
        </button>
        <div className="dropdown-menu" aria-labelledby="navbarDropdown">
          <a className="dropdown-item" href="/profile">My Profile</a>
          <a className="dropdown-item" href="#">Another action</a>
          <div className="dropdown-divider"></div>
          <a className="dropdown-item" style={{cursor:'pointer'}} onClick={this.user}>Log out</a>
        </div>   
      </div>) 
    }
  }
  url = () =>{
    if(this.state.user !== 'False'){
      return "/create/product/";
    }
    else{
      return "/login/";
    }
  }
  url2(){
    if(this.state.user !== 'False'){
      return "/announcement";
    }
    else{
      return "/login/";
    }
  }
  lang1(){
    if(localStorage.getItem('lang') === 'en'){
        
        return LOCALES.English
    }
    else if(localStorage.getItem('lang')=== 'ru'){
        return LOCALES.Russian 
    }
    else if(localStorage.getItem('lang')=== 'uz'){
        return LOCALES.Uzbek
    }
    else{
        return LOCALES.Uzbek
    }
}
  
    render() {
      const lang = localStorage.getItem('lang') || 'en'
        return (
        <I18nProvider locale = {this.lang1()}>
        <div>
                    <nav className="navbar navbar-expand-md bg-light navbar-light ">
          <a className="navbar-brand" href="#">{translate('logo')}</a>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsibleNavbar">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="collapsibleNavbar">
            <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
              <li className="nav-item">
                <Link to="/"><div className ='nav-link'>{translate('home')}</div></Link>
              </li>
              <li className="nav-item">
                <Link to="/product_list"><div className='nav-link'>{translate('list')}</div></Link>
              </li>
              <li className="nav-item">
                <Link to="/contact"><div className="nav-link">{translate('contact')}</div></Link>
              </li>
              <li className="nav-item">
                <Link to="/animation/"><div className="nav-link">{translate('animation')}</div></Link>
              </li> 
            
              {this.state.user !== 'False' ?
              <React.Fragment>
                <li className="nav-item">
                  <Link to={this.url()}><div className="nav-link">Create an Announce</div></Link>
                </li> 
                <li className="nav-item">
                  <Link to={this.url2}><div className="nav-link">Your Announcement</div></Link>
                </li>
              </React.Fragment>
              : ''}
            {this.state.user !== 'False' ? <React.Fragment>
                <li className="nav-item">
                  <Link to='/cart/' ><div className="nav-link">Cart <div className="badge badge-info"> {this.props.state.cart} </div></div></Link>
                </li>
                <li className="nav-item">
                  <Link to='/wishlist/'><div className="nav-link">Wishlist <div className="badge badge-info">{this.props.state.wishlist}</div> </div></Link>
                </li></React.Fragment>
              : ''}
            </ul>
            <div className="form-inline my-2 my-lg-0">
                  <br/>
            <div>{this.authenticated()}</div>
            
            </div>
            <div className="form-inline my-2 my-lg-0">
            
          <div class="dropdown show">
            <a style={{textDecoration:'none',color:'black'}} class="dropdown-toggle mr-5 p-1" href="#" role="button" id="dropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
              {lang}
            </a>

            <div class="dropdown-menu" aria-labelledby="dropdownMenuLink">
              <div style={{cursor:'pointer'}} onClick = {()=>this.props.lang2("en")} class="dropdown-item">English</div>
              <div style={{cursor:'pointer'}} onClick = {()=>this.props.lang2("ru")} class="dropdown-item">Russian</div>
              <div style={{cursor:'pointer'}} onClick = {()=>this.props.lang2("uz")} class="dropdown-item">Uzbek</div>
            </div>
          </div>
              </div>
            </div>  
          </nav>
        </div>
        </I18nProvider>
        );
    }
}
 
export default Navbar;
