import React, { Component } from 'react';
import axios from 'axios'
import cookie from 'react-cookies'
import Navbar from './compenents/Navbar'
import { Redirect } from 'react-router';
class Login extends Component {

    constructor(props)
    {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handlefield = this.handlefield.bind(this);
        this.clearform = this.clearform.bind(this);
        this.clearform = this.clearform.bind(this);
        this.lang2 = this.lang2.bind(this)
    }
    state = {
        username : null,
        password:undefined,
        success:'',
        login:false,
        lang:''
    }
    componentDidMount(){

            axios({
                method:'POST',
                url:'http://127.0.0.1:8000/check_l/',
                headers:{
                    'Content-Type':'application/json',
                   
                },
                credentials:'include'
            })
                .then(response =>{
                    this.setState({
                        login:response.data
                    })
                })
                axios.get('http://127.0.0.1:8000/sdlkewrws/')
                .then(response => {
                    this.setState({
                    cart:response.data.cart,
                    wishlist:response.data.wishlist
                    })
                })
    }    
    empty = () =>{
        setTimeout(() => {
            this.setState({
                success:''
            })
        }, 15000);
    }
    clearform(){
        this.PostCreateForm.reset();
    }
    handleSubmit(event){
        event.preventDefault();
        let comp = this;
        var formData = new FormData()
        formData.append('username',this.state.username);
        formData.append('password',this.state.password);
        axios({
            method:'POST',
            url:'http://127.0.0.1:8000/sadfasd/',
            headers:{
                'Content-Type':'application/json',
           
            },
            data:formData,
            credentials:'include'
        })
        .then(response => {
            console.log(response)
            if(response.data=== 'True')
            {
                comp.setState({
                    login:true
                })
            }
            else{
                console.log(response.data)
                comp.setState({
                    success:response.data
                })
            }
            
        })
        .catch(errors => {
            console.log(errors,"Erzrp")
            comp.setState({
                success:"Something Went Wrong!"
            })
     
        })
        this.clearform()
    }
    lang2(ev){
        localStorage.setItem('lang',ev);
        this.setState({
            lang:ev
        })
    
      }
    handlefield(event)
    {
        event.preventDefault();
        this.setState({
            [event.target.name]:event.target.value,
        })
        
    }
    
    render() {
        let csrf = cookie.load('csrftoken');
      
            if(this.state.login)
            {
                return <Redirect to='/' />
            }
            else{
                return ( 
            
                    <div>
                        <Navbar state={this.state} lang2 = {this.lang2} /> <br/>
                     
                     
                     <form onSubmit={this.handleSubmit} ref = {(el) => this.PostCreateForm = el} className="container">
                     <h2>Log in Page</h2><br/>
                     <div >{this.state.success !== '' ? <div className='alert alert-danger'>{this.state.success}</div> : this.empty()}   </div>
                         <label> Foydalanuvchi nomi:</label>
                              <input className='form-control' type="text" name="username" id="" placeholder='Sizning Foydalanuvchi nomingiz' onChange={this.handlefield} required = 'required' />
                         <br/>
                         <label>Parol:</label>
                              <input className='form-control' type="password" name="password" placeholder='Password' onChange={this.handlefield} required = 'required' />
                         <br/>
                              <br/>
                              
                         <button type='submit' className='btn btn-info'>Submit</button>
                         
                     </form>
                 </div> 
                  );
            }
              
      

    }
}
 
export default Login ;
