import axios from 'axios';
import React, { Component } from 'react';
import cookie from 'react-cookies'
import { Redirect } from 'react-router';
import Navbar from './compenents/Navbar';

class UpdateProduct extends Component {
    constructor(props){
        super(props);
        this.state = {
            name:null,
            color:null,
            desc:null,
            price:null,
            sale:false,
            price_in_sale:2,
            quantity:null,
            img:undefined,
            size:null,
            category:null,
            brand:null,
            cart:0,
            wishlist:0,
            csrfmiddlewaretoken:cookie.load('csrftoken')
        }
        this.handleForm = this.handleForm.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleFileForm = this.handleFileForm.bind(this);
        this.price = this.price.bind(this)
        this.clear = this.clear.bind(this)
    }
    componentDidMount(){
        let id = this.props.match.params.id;
        axios.get('/detail/' + id + '/')
        .then(response => {
            this.setState({
                name :response.data.name,
                color :response.data.color,
                desc :response.data.desc,
                price :response.data.price,
                sale :response.data.sale,
                price_in_sale :response.data.price_in_sale,
                quantity :response.data.quantity,
                img :response.data.img,
                size :response.data.size,
                category :response.data.category,
                brand :response.data.brand,
            })
        })
    }
    handleForm(event) {
        let comp = this;
        console.log(event.target.name,event.target.value)
        if(event.target.name === 'sale'){
            if(event.target.checked === true)
            {
                comp.setState({
                    sale:null
                })
                comp.setState({
                    sale:true
                })
                console.log("Sale:False")
            }
            else{
                comp.setState({
                    sale:null
                })
                comp.setState({
                    sale:false
                   })
                console.log("Sale:True")
            }
        }
        else{
            this.setState({
                [event.target.name]:null
            })
            this.setState({
                [event.target.name]:event.target.value,
            })
            console.log('not checkbox')
        }


    }
    price(){
        if(this.state.sale)
        {
            return ( <div>
                 <label htmlFor="price">Price In Sale</label>
                <input type="number" value='1' name="price_in_sale" value={this.state.price_in_sale} readOnly className='form-control' onChange={this.handleForm} />
            </div> )
        }
    }

    handleSubmit(event){
        let id = this.props.match.params.id;
        let csrf = cookie.load('csrftoken');
        var formData = new FormData();
        console.log(this.state.img)
        console.log(typeof(this.state.img))
        if(typeof(this.state.img) !== typeof("AAAA")){
            console.log('Not String')
            formData.append('img',this.state.img)
        }
        formData.append('name', this.state.name)
        formData.append('color', this.state.color)
        formData.append('desc', this.state.desc)
        formData.append('price', this.state.price)
        formData.append('price_in_sale', this.state.sale ? this.state.price_in_sale : 2)
        formData.append('sale', this.state.sale)
        formData.append('quantity', this.state.quantity)
        formData.append('size', this.state.size)
        formData.append('category', this.state.category)
        formData.append('brand', this.state.brand)
        event.preventDefault();
        console.log(formData)
        axios({
            method:'PUT',
            url:'/detail/' + id + '/',
            headers:{
                "Content-Type": "multipart/form-data",
                'X-CSRFToken':csrf
            },
            data:formData,
            credentials:'include'
        })
        .catch(errors =>{
                console.log(errors)
            })
        axios.get('/sdlkewrws/')
        .then(response => {
            this.setState({
            cart:response.data.cart,
            wishlist:response.data.wishlist
            })
        })
        this.clear()
        this.componentDidMount()
    }
    clear()
    {
        this.ppp.reset();
    }
    handleFileForm(event) 
    {
        event.preventDefault();
        let files = event.target.files;
        this.setState({
            img: files[0]
        }, () => console.log(this.state));
    }
    count(){
        axios.get('/sdlkewrws/')
        .then(response => {
        this.setState({
            cart:response.data.cart,
            wishlist:response.data.wishlist
        })
        })
    }
    render() { 
        console.log(this.state.img)
        return ( 
           <div>
               <Navbar state = {this.state} /><br/> <br/>
                <div className='container'>
               <form  onSubmit={this.handleSubmit} className='container' ref={(el) => this.ppp = el} encType="multipart/form-data">
                    <label htmlFor='name'>Product Name</label>
                    <input type="text" id='name' defaultValue={this.state.name}  name="name" className='form-control' onChange={this.handleForm} required='required' />
                    <label htmlFor="quantity">Quantity</label>
                    <input type="number" id='quantity' defaultValue={this.state.quantity} className='form-control' name="quantity" required='required' onChange={this.handleForm}/>
                    <label htmlFor="price">Price</label>
                    <input type="number" id='price' name="price" defaultValue={this.state.price} readOnly className='form-control' onChange={this.handleForm} />
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck" name="sale" checked={this.state.sale} onChange={this.handleForm} />
                        <label className="custom-control-label" htmlFor="customCheck"> Sale</label>
                    </div>
                    {this.price()}
                    <label htmlFor="img">Main Image of Product: </label> 
                    <input type="file" name='img' accept='.jpg,.png,.svg'  defaultValue={this.state.img}  onChange={this.handleFileForm}/> <label onClick={this.handleSubmit} className='btn btn-sm btn-info'>Preview</label> <br/>
                     <label> <img src={this.state.img} width='100px' height='100px' /></label><br/>
                    <label htmlFor="size">Available sizes</label>
                    <input type="text"  name="size" className='form-control' defaultValue={this.state.size} onChange={this.handleForm} />
                    <label htmlFor="color">Color of Product</label>
                    <input type="text"  name="color" className='form-control' defaultValue={this.state.color} onChange={this.handleForm} />
                    <label htmlFor="desc">Full Description</label>
                    <textarea  cols="30" rows="10" name="desc" defaultValue={this.state.desc} className='form-control' onChange={this.handleForm}></textarea>
                    <label htmlFor="category">Category</label>
                    <select name="category" defaultValue={this.state.category} class="custom-select mb-3" onChange={this.handleForm}>
                        <option value="Fashion & Beauty">Fashion & Beauty</option>
                        <option value="Kids & Babies Clothes">Kids & Babies Clothes</option>
                        <option value="Men & Women  Clothes">Men & Women  Clothes</option>
                        <option value="Gadgets & Accessories">Gadgets & Accessories</option>
                        <option value="Electronics & Accessories">Electronics & Accessories</option>
                    </select>
                    <label htmlFor="brand">Brand</label>
                    <input type="text" name="brand" defaultValue={this.state.brand} className='form-control' onChange={this.handleForm}  />
                   <br/><br/> <input type="submit" onClick={this.handleSubmit} className="btn btn-info" />

                </form>
            </div>
           </div>
         );
    }
}
 
export default UpdateProduct;
