import React, { Component } from 'react';
import Navbar from './compenents/Navbar'
import axios from 'axios'
import Detail1 from './compenents/detail1'
import {Redirect} from 'react-router-dom'

class Detail extends Component {
    constructor(props){
        super(props);
        this.state = {
            slug:null,
            valid:false,
            array:[],
        }
    }
    componentDidMount()
    {   
        
        let comp = this;
        comp.setState({
            slug:null,
        })
        if(comp.props.match)
        {
           const slug = comp.props.match.params.slug
           comp.setState({
               slug:slug
           })
           let url = "/detail/" + slug;
           axios.get(url)
           .then(response=>{
            comp.setState({
                valid:true,
                array:response.data
            }) 
            console.log(comp.state.array)
           })
           
           .catch(errors =>{
               console.log(errors)
           })
        }
        else{
            console.log("There is not any slug")
        }
    }
    

    valid = ()=>{
        console.log(this.state.valid)
        if(this.state.valid === true){
            return <Detail1 pro={this.state.array}/>;
        }
        else{
            return <Redirect to='/notfound' />
        }
    }
   
    render() {
        return (
        <div>
            <Navbar />
            {this.valid()}
        </div>
        );
    }
}
 
export default Detail;
