import React, { Component } from 'react';
import cookie from 'react-cookies'
import axios from 'axios'
import Loading from './loading';

class Navbar extends Component {
  state = {
    user:[]
  }
  componentDidMount(){
    let cpomp = this;
    axios.get('/logggedin/')
      .then(response => {
        cpomp.setState({
          user:response.data
        })
      })
        .catch(errors =>{
          console.log(errors)
        })  
  }
  componentDidUpdate(){
    setTimeout(() => {
      let cpomp = this;
    axios.get('/logggedin/')
      .then(response => {
        cpomp.setState({
          user:response.data
        })
      })
        .catch(errors =>{
          console.log(errors)
        })  
    },1000);
  }
  authenticated = () => {
    if(this.state.user === 'False')
    {
      return <div> <a href="/login">Log in</a> </div>
    }
    else{
      return  (
      <div>  
         <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
             <i class="fas fa-user"></i> {this.state.user["username"]} 
        </a>
        <div class="dropdown-menu float-right" aria-labelledby="navbarDropdown">
          <a class="dropdown-item" href="#">Action</a>
          <a class="dropdown-item" href="#">Another action</a>
          <div class="dropdown-divider"></div>
          <a class="dropdown-item" href="#">Something else here</a>
        </div>   
      </div>) 
    }
  }
  url = () =>{
    if(this.state.user !== 'False'){
      return "/create/product/";
    }
    else{
      return "/login/";
    }
  }
    render() {
      console.log(this.state.user) 
        return (<div>
            <nav class="navbar navbar-expand-md bg-light navbar-light ">
  <a class="navbar-brand" href="#">React Market</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsibleNavbar">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="collapsibleNavbar">
    <ul class="navbar-nav mr-auto mt-2 mt-lg-0">
      <li class="nav-item">
        <a class="nav-link" href="/">Home</a>
      </li>
      <li class="nav-item">
        <a class="nav-link" href="/product_list">Product List</a>
      </li>
      <li class="nav-item">
        <a class="nav-link" href="/contact">Contact</a>
      </li>    
      <li class="nav-item">
        <a class="nav-link" href={this.url()}> Create an Announce</a>
      </li> 
    </ul>
    <div class="form-inline my-2 my-lg-0">
     <div>{this.authenticated()}</div>
    </div>
  </div>  
</nav>
        </div>);
    }
}
 
export default Navbar;
