import React, { Component } from 'react';

class Contact extends Component {
    state = {  }


    handleSubmit(event){
        console.log(event);
    }
    render() { 
        return ( 
           <div>
               <form onSubmit={this.handleSubmit()}>
                   <label> Ismingiz</label>
                        <input className='form-control' type="text" name="name" id="" placeholder='Sizning Ismingiz' />
                   <br/>
                   <label> Telefon Nomer</label>
                        <input className='form-control' type="number" name="phone" placeholder='Sizning Ismingiz' />
                   <br/>
                   <label> Sizning Xabaringiz</label> <br/>
                        <textarea className='form-control' name="msg" cols="50" rows="10"></textarea>
                    
                        <br/>
                        
                   <button type='submit' className='btn btn-info'>Submit</button>
                   
               </form>
           </div> 
         );
    }
}
 
export default Contact ;
