import React, { Component } from 'react';

class CreateProduct extends Component {
    constructor(props){
        super(props);
        this.state = {
            name:null,
            color:null,
            desc:null,
            price:null,
            sale:false,
            price_in_sale:null,
            quantity:null,
            img:undefined,
            size:null,
            date:undefined,
            category:null,
            brand:null,
        }
        this.handleForm = this.handleForm.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleForm(event) {
        event.preventDefault();
        console.log(event.target.name,event.target.value)
        this.setState({
            [event.target.name]:event.target.value,
        })
    }
    handleSubmit(event){
        event.preventDefault();
    }
    price(event){
        if(event.target.value === 'on'){
            return (<div><label htmlFor="price_in_sale">Price in Sale</label> <input type="number" name="price_in_sale" /></div>)
        }
        else{
            return (<div><label htmlFor="price">Price</label><input type="number" name="price" /></div>);
        }
    }
    render() { 
        return ( 
            <div  style={{backgroundColor:'lavender'}}>
               <center> <form  onSubmit={this.handleSubmit} style={{width:'200px',marginTop:'10%',backgroundColor:'white'}}>
                    <label htmlFor='name'>Product Name</label>
                    <input type="text" value={this.state.name} name="name" className='form-control' onChange={this.handleForm} required='required' />
                    <label htmlFor="quantity">Quantity</label>
                    <input type="number" value={this.state.quantity} className='form-control' name="quantiy" required='required' onChange={this.handleForm}/>
                    <label htmlFor="sale">Sale</label>
                    <input type="checkbox" onClick={this.price} className="" name='sale' required='required'onChange={this.handleForm} />
                </form></center>
            </div>
         );
    }
}
 
export default CreateProduct;
