import axios from 'axios';
import React, { Component } from 'react';
import cookie from 'react-cookies'
import { Redirect } from 'react-router';
import Navbar from './Navbar';

class CreateProduct extends Component {
    constructor(props){
        super(props);
        this.state = {
            name:null,
            color:null,
            desc:null,
            price:null,
            sale:false,
            price_in_sale:null,
            quantity:null,
            img:undefined,
            size:null,
            category:null,
            brand:null,
            csrfmiddlewaretoken:cookie.load('csrftoken')
        }
        this.handleForm = this.handleForm.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleFileForm = this.handleFileForm.bind(this);
        this.price = this.price.bind(this)
        this.clear = this.clear.bind(this)
    }
    handleForm(event) {
        let comp = this;
        console.log(event.target.name,event.target.value)
        if(event.target.name === 'sale'){
            if(event.target.checked === true)
            {
                comp.setState({
                    sale:true
                })
                console.log("Sale:False")
            }
            else{
                comp.setState({
                    sale:false
                   })
                console.log("Sale:True")
            }
        }
        else{
            this.setState({
                [event.target.name]:event.target.value,
            })
            console.log('not checkbox')
        }


    }
    price(){
        if(this.state.sale)
        {
            return ( <div>
                 <label htmlFor="price">Price In Sale</label>
                <input type="number" name="price_in_sale" className='form-control' onChange={this.handleForm} />
            </div> )
        }
    }

    handleSubmit(event){
        let csrf = cookie.load('csrftoken');
        var formData = new FormData();
        formData.append('img', this.state.img)
        formData.append('name', this.state.name)
        formData.append('color', this.state.color)
        formData.append('desc', this.state.desc)
        formData.append('price', this.state.price)
        formData.append('price_in_sale', this.state.price_in_sale)
        formData.append('sale', this.state.sale)
        formData.append('quantity', this.state.quantity)
        formData.append('img', this.state.img)
        formData.append('size', this.state.size)
        formData.append('sale', this.state.sale)
        formData.append('category', this.state.category)
        formData.append('brand', this.state.brand)
        event.preventDefault();
        console.log(this.state)
        axios({
            method:'POST',
            url:'/api1/',
            headers:{
                "Content-Type": "multipart/form-data",
                'X-CSRFToken':csrf
            },
            data:formData,
            credentials:'include'
        })
        .catch(errors =>{
                console.log(errors)
            })
        this.clear()
    }
    clear()
    {
        this.ppp.reset();
    }
    handleFileForm(event) 
    {
        event.preventDefault();
        let files = event.target.files;
        this.setState({
            img: files[0]
        }, () => console.log(this.state));
    }
   
    render() { 
        return ( 
           <div>
               <Navbar /><br/> <br/>
                <div className='container'>
               <form  onSubmit={this.handleSubmit} className='container' ref={(el) => this.ppp = el} encType="multipart/form-data">
                    <label htmlFor='name'>Product Name</label>
                    <input type="text" id='name'  name="name" className='form-control' onChange={this.handleForm} required='required' />
                    <label htmlFor="quantity">Quantity</label>
                    <input type="number" id='quantity' className='form-control' name="quantity" required='required' onChange={this.handleForm}/>
                    <label htmlFor="price">Price</label>
                    <input type="number" id='price' name="price" className='form-control' onChange={this.handleForm} />
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck" name="sale" checked={this.state.sale} onChange={this.handleForm} />
                        <label className="custom-control-label" htmlFor="customCheck"> Sale</label>
                    </div>
                    {this.price()}
                    <label htmlFor="img">Main Image of Product </label>
                    <input type="file" name='img' accept='.jpg,.png,.svg'  onChange={this.handleFileForm}/><br/>
                    <label htmlFor="size">Available sizes</label>
                    <input type="text"  name="size" className='form-control' onChange={this.handleForm} />
                    <label htmlFor="color">Color of Product</label>
                    <input type="text"  name="color" className='form-control' onChange={this.handleForm} />
                    <label htmlFor="desc">Full Description</label>
                    <textarea  cols="30" rows="10" name="desc" className='form-control' onChange={this.handleForm}></textarea>
                    <label htmlFor="category">Category</label>
                    <select name="category" class="custom-select mb-3" onChange={this.handleForm}>
                        <option selected>---------</option>
                        <option value="Fashion & Beauty">Fashion & Beauty</option>
                        <option value="Kids & Babies Clothes">Kids & Babies Clothes</option>
                        <option value="Men & Women  Clothes">Men & Women  Clothes</option>
                        <option value="Gadgets & Accessories">Gadgets & Accessories</option>
                        <option value="Electronics & Accessories">Electronics & Accessories</option>
                    </select>
                    <label htmlFor="brand">Brand</label>
                    <input type="text" name="brand" className='form-control' onChange={this.handleForm}  />
                   <br/><br/> <input type="submit" className="btn btn-info" />

                </form>
            </div>
           </div>
         );
    }
}
 
export default CreateProduct;
