import React, { Component } from 'react';
import axios from 'axios'
class Contact extends Component {
    constructor(props)
    {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handlefield = this.handlefield.bind(this);
        this.data1 = this.data1.bind(this);
        this.clearform = this.clearform.bind(this);
    }
    state = {
        name : null,
        number:null,
        msg:null,
        success:'',
        errors:null
    }
    data1(data){
        console.log(data)
            return <div style={{color:'red'}}>
                        {this.state.success}
          </div>
    }
            
    empty = () =>{
        setTimeout(() => {
            this.setState({
                success:''
            })
        }, 9000);
    }
    clearform(){
        this.PostCreateForm.reset();
    }
    handleSubmit(event){
        let comp = this
        event.preventDefault();
        axios.post('contact/',this.state)
        .then(response => {
            console.log(response)
            comp.setState({
                success:response.data
            })
            this.data1(response.data)
            this.clearform()
        })
        .catch(errors => {
            comp.setState({
                success:"Something Went Wrong!"
            })
            console.log(errors)
        })
        console.log(this.state)
        console.log('Handle Submit');
    }
    handlefield(event)
    {
        event.preventDefault();
        this.setState({
            [event.target.name]:event.target.value,
        })
        
    }
    render() { 
        return ( 
           <div>
                <nav class="navbar navbar-expand-md bg-dark navbar-dark">
  <a class="navbar-brand" href="#">Navbar</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsibleNavbar">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="collapsibleNavbar">
    <ul class="navbar-nav">
      <li class="nav-item">
        <a class="nav-link" href="/">Home</a>
      </li>
      <li class="nav-item">
        <a class="nav-link" href="/product_list">Product List</a>
      </li>
      <li class="nav-item">
        <a class="nav-link" href="/contact">Contact</a>
      </li>    
    </ul>
  </div>  
</nav><br/>
            
               <div>{this.data1()}{this.empty()}   </div>
               <form onSubmit={this.handleSubmit} ref = {(el) => this.PostCreateForm = el} className="container">
               <h2>Contact Page</h2>
                   <label> Ismingiz</label>
                        <input className='form-control' type="text" name="name" id="" placeholder='Sizning Ismingiz' onChange={this.handlefield} required = 'required' />
                   <br/>
                   <label> Telefon Nomer</label>
                        <input className='form-control' type="number" name="number" placeholder='Sizning Raqamingiz' onChange={this.handlefield} required = 'required' />
                   <br/>
                   <label> Sizning Xabaringiz</label> <br/>
                        <textarea className='form-control' name="msg" cols="50" rows="10" onChange={this.handlefield} required = 'required'></textarea>
                    
                        <br/>
                        
                   <button type='submit' className='btn btn-info'>Submit</button>
                   
               </form>
           </div> 
         );
    }
}
 
export default Contact ;
