import React, { Component } from 'react';
import Navbar from './compenents/Navbar'
import axios from 'axios'
import Detail1 from './compenents/detail1'

class Detail extends Component {
    constructor(props){
        super(props);
        this.state = {
            slug:null,
            valid:False,
            array:[],
        }
    }
    componentDidMount()
    {   
        
        let comp = this;
        comp.setState({
            slug:null,
        })
        if(comp.props.match)
        {
           const slug = comp.props.match.params.slug
           comp.setState({
               slug:slug
           })
           let url = "/detail/" + slug;
           axios.get(url)
           .then(response=>{
            comp.setState({
                valid:true,
                array:response.data
            }) 
            console.log(comp.state.array)
           })
           
           .catch(errors =>{
               console.log(errors)
           })
        }
        else{
            console.log("There is not any slug")
        }
    }
    

    valid(){
        if(this.state.valid){
            return   <Detail1 pro={this.state.array}/>;
        }
        else{
            <Redirec to='/404' />
        }
    }
   
    render() {
        return (
        <div>
            <Navbar />
          
        </div>
        );
    }
}
 
export default Detail;
