import logo from './logo.svg';
import './App.css';
import cookie from 'react-cookies'
import 'whatwg-fetch'
import React, { Component,useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import PostInline from './compenents/post'

class App extends Component {
  state = {
    posts:[],
  }
  componentDidMount()
  {
    this.setState({
      posts:[],
    })
    fetch('api1/')
    .then((response) => response.json())
    .then(booksList => {
     if(this.state.posts != booksList)
     {
      this.setState({ posts: booksList });
     }  
    });
  }
componentDidUpdate(){
  setTimeout(() => {
    fetch('api1/')
    .then((response) => response.json())
    .then(booksList => {
      console.log(booksList);
        this.setState({ posts: booksList });
    });
  }, 1000);
}

  render()
  {
    return (
      <div className='container'>
        <ul class="nav nav-pills" role="tablist">
    <li class="nav-item">
      <a class="nav-link active" data-toggle="pill" href="#home">Home</a>
    </li>
    <li class="nav-item">
      <a class="nav-link" data-toggle="pill" href="#productlist">Menu 1</a>
    </li>
    <li class="nav-item">
      <a class="nav-link" data-toggle="pill" href="#contact">Menu 2</a>
    </li>
  </ul>


  <div class="tab-content">
    <div id="home" class="container tab-pane active"><br/>
      <h3>HOME</h3>
      <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
    </div>
    <div id="productlist" class="container tab-pane fade"><br/>
      <h3>Product List </h3>
      <div className='container'><div className='row'>{this.state.posts.map(post => <PostInline key = {post.id} post = {post} /> )}</div></div> 
    </div>
    <div id="menu2" class="container tab-pane fade"><br/>
      <h3>Contact</h3>
      <p>Sed ut perspiciatis unde omnis iste natus error sit voluptatem accusantium doloremque laudantium, totam rem aperiam.</p>
    </div>
  </div>
            <br/>
            <br/>
            <br/>

      </div>
    );
  }
}


export default App;
