import cookie from 'react-cookies'
import 'whatwg-fetch'
import React, { Component,useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import PostInline from './compenents/post'
import Contact from './compenents/contact'
import {BrowserRouter,Route,Redirect,Switch} from 'react-router-dom'

class List extends Component {
    state = {
        posts:[],
      }
      componentDidMount()
      {
        this.setState({
          posts:[],
        })
        fetch('http://127.0.0.1:8000/api1/')
        .then((response) => response.json())
        .then(booksList => {
         if(this.state.posts != booksList)
         {
          this.setState({ posts: booksList });
         }  
        });
      }
// componentDidUpdate(){
//   setTimeout(() => {
//     fetch('api1/')
//     .then((response) => response.json())
//     .then(booksList => {
//       console.log(booksList);
//         this.setState({ posts: booksList });
//     });
//   }, 1000);
// }
    render() { 
        return ( 
            <div>
              <nav class="navbar navbar-expand-md bg-dark navbar-dark">
  <a class="navbar-brand" href="#">React Market</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsibleNavbar">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="collapsibleNavbar">
    <ul class="navbar-nav">
      <li class="nav-item">
        <a class="nav-link" href="/">Home</a>
      </li>
      <li class="nav-item">
        <a class="nav-link" href="/product_list">Product List</a>
      </li>
      <li class="nav-item">
        <a class="nav-link" href="/contact">Contact</a>
      </li>    
    </ul>
  </div>  
</nav><br/>
   <div className="container">
   <h3>Product List </h3><br/>
       <div className='container'><div className='row'>{this.state.posts.map(post => <PostInline key = {post.id} post = {post} /> )}</div></div> 

   </div>
            </div>
         );
    }
}
 
export default List;
