import React, { Component } from 'react';
import { Spring,useSpring,animated } from 'react-spring'

function App() {
    const greeting = 'Hello Function Component!';
    const props1 = useSpring({
        from:{
            opacity:0,
            marginTop:-500
        },
        to:{
            opacity:1,
            marginTop:0
        }
    })
    const props2 = useSpring({
        from:{
            opacity:0,
            marginTop:-500
        },
        to:{
            opacity:1,
            marginTop:0
        },
        config:{
            delay:1000,
            duration:1000
        }
    })
    return  (
                <div>
                    <animated.div style={props1} className='container'>
                        <div style={{width:'300px',padding:'10px',borderRadius:'15px',backgroundColor:'lavender',wordBreak:'break-all'}}> Lorem ipsum dolor sit amet consectetur, adipisicing elit. Fugit, accusantium fuga magni quod cum neque ex? Possimus voluptate facilis dolores. Blanditiis, architecto. Ad quasi, dolorem repellendus rerum itaque debitis repudiandae. </div>
                    </animated.div>
                    <animated.div style={props2} className='container'>
                        <div style={{width:'300px',padding:'10px',borderRadius:'15px',backgroundColor:'lavender',wordBreak:'break-all'}}> Lorem ipsum dolor sit amet consectetur, adipisicing elit. Fugit, accusantium fuga magni quod cum neque ex? Possimus voluptate facilis dolores. Blanditiis, architecto. Ad quasi, dolorem repellendus rerum itaque debitis repudiandae. </div>
                    </animated.div>
                </div>
            );
  }
   
  export default App;
