import React, { Component } from 'react';
import axios from 'axios'
class Contact extends Component {
    constructor(props)
    {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handlefield = this.handlefield.bind(this);
        this.data1 = this.data1.bind(this)
    }
    state = {
        name : null,
        number:null,
        msg:null,
        success:'',
        errors:null
    }
    data1(data){
        
        console.log(data)
        setInterval(() => {
            return <div style={{color:'red'}}>{this.state.success}</div>
        }, 2000);
        return <div style={{color:'red'}}>{this.state.success}</div>
    }

    handleSubmit(event){
        let comp = this
        event.preventDefault();
        axios.post('contact/',this.state)
        .then(response => {
            console.log(response)
            comp.setState({
                success:response.data
            })
            this.data1(response.data)
        })
        .catch(errors => {
            comp.setState({
                success:"Something Went Wrong!"
            })
            console.log(errors)
        })
        console.log(this.state)
        console.log('Handle Submit');
    }
    handlefield(event)
    {
        event.preventDefault();
        this.setState({
            [event.target.name]:event.target.value,
        })
        
    }
    render() { 
        return ( 
           <div>
               <div>{this.data1()}   </div>
               <form onSubmit={this.handleSubmit}>
                   <label> Ismingiz</label>
                        <input className='form-control' type="text" name="name" id="" placeholder='Sizning Ismingiz' onChange={this.handlefield} required = 'required' />
                   <br/>
                   <label> Telefon Nomer</label>
                        <input className='form-control' type="number" name="number" placeholder='Sizning Ismingiz' onChange={this.handlefield} required = 'required' />
                   <br/>
                   <label> Sizning Xabaringiz</label> <br/>
                        <textarea className='form-control' name="msg" cols="50" rows="10" onChange={this.handlefield} required = 'required'></textarea>
                    
                        <br/>
                        
                   <button type='submit' className='btn btn-info'>Submit</button>
                   
               </form>
           </div> 
         );
    }
}
 
export default Contact ;
