import React, { Component } from 'react';
class PostInline extends Component {
    sale = () =>{
        if(this.props.post.sale)
        {
            return <div>{this.props.post.price_in_sale} So'm</div>
        }
        else{
            return <div>{this.props.post.price} So'm</div>
        }
    }
    href(){
        return "/product/detail/" + this.props.post.id;
    }
    render() { 
        return (
        <div className = 'col-md-4' style={{overflow:'hidden'}}>
                    <div className="card" style={{width:'100%'}}>
                        <img className="card-img-top" src={this.props.post.img} style={{height:300}} />
                        <div className="card-body">
                        <a href={this.href()}><h4 className="card-title"  style={{height:'60px',overflow:'hidden',wordBreak:'break-all'}}>{this.props.post.name}</h4></a>
                        <p className="card-text"><strong>{this.sale()}</strong></p>
                        <a  className="btn btn-primary">Buy Now</a>
                        </div>
                    </div><br/>
            
        </div> 
        );
    }
}
 
export default PostInline;
