import React, { Component } from 'react';
import logo from './logo.svg';
class PostInline extends Component {
    render() { 
        return (
        <div className = 'col-md-4' style={{overflow:'hidden'}}>
                    <div className="card" style={{width:'100%'}}>
                        <img className="card-img-top" src={this.props.post.img} style={{height:300}} />
                        <div className="card-body">
                        <h4 className="card-title" style={{height:'60px',overflow:'hidden',wordBreak:'break-all'}}>{this.props.post.name}</h4>
                        <p className="card-text">{this.props.post.price}</p>
                        <a href="#" className="btn btn-primary">See Profile</a>
                        </div>
                    </div>
            
        </div> 
        );
    }
}
 
export default PostInline;
