import React, { Component } from 'react';
import logo from './logo.svg';
class PostInline extends Component {
    render() { 
        return (
        <div>
                    <div className="card" style={{width:'100%'}}>
                        <img className="card-img-top" src={this.props.post.img} style={{width:200}} />
                        <div className="card-body">
                        <h4 className="card-title">{this.props.post.name}</h4>
                        <p className="card-text">{this.props.post.price}</p>
                        <a href="#" className="btn btn-primary">See Profile</a>
                        </div>
                    </div>
                    <br/>
            
        </div> 
        );
    }
}
 
export default PostInline;
