import React, { Component } from 'react';
import cookie from 'react-cookies'
import axios from 'axios'
import Loading from './loading';
import {
  Link
} from "react-router-dom";

class Navbar extends Component {
  constructor(props){
    super(props);
    this.user = this.user.bind(this)
    this.url2 = this.url2.bind(this)
  }
  state = {
    user:[],
    logout:false,
  }

  componentDidMount(){
    let cpomp = this;
    axios.get('/logggedin/')
      .then(response => {
        cpomp.setState({
          user:response.data
        })
      })
        .catch(errors =>{
          console.log(errors)
        })
  }
  user(){
    let cpomp = this;
    axios.get('/logout1/')
      .then(response => {
        cpomp.setState({
          user:response.data
        })
      })
        .catch(errors =>{
          console.log(errors)
        })  
  }


  authenticated = () => {
    if(this.state.user === 'False')
    {
      return (
      <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
            <li className="nav-item">
                <Link to={this.props.match.params.lang +'/login'}><div className='nav-link'>Login</div></Link>
             </li> 
            <li class="nav-item">
                <Link to={this.props.match.params.lang +'/signup'}><div className='nav-link'>Register</div></Link>
            </li> 
      </ul>)
    }
    else{
      return  (
      <div className='dropdown' style={{marginRight:'70px'}}>  
         <button class=" btn btn-light dropdown-toggle" href="#" id="navbarDropdown" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            <i class="fas fa-user"></i> {this.state.user["username"]} 
        </button>
        <div class="dropdown-menu" aria-labelledby="navbarDropdown">
          <a class="dropdown-item" href={this.props.match.params.lang + '/profile' }>My Profile</a>
          <a class="dropdown-item" href="#">Another action</a>
          <div class="dropdown-divider"></div>
          <a class="dropdown-item" style={{cursor:'pointer'}} onClick={this.user}>Log out</a>
        </div>   
      </div>) 
    }
  }
  url = () =>{
    if(this.state.user !== 'False'){
      return this.props.match.params.lang + '/create/product/' ;
    }
    else{
      return this.props.match.params.lang + '/login/' ;
    }
  }
  url2(){
    if(this.state.user !== 'False'){
      return "/announcement";
    }
    else{
      return "/login/";
    }
  }
    render() {
        return (<div>
            <nav class="navbar navbar-expand-md bg-light navbar-light ">
  <a className="navbar-brand" href="#">React Market</a>
  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsibleNavbar">
    <span className="navbar-toggler-icon"></span>
  </button>
  <div className="collapse navbar-collapse" id="collapsibleNavbar">
    <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
      <li className="nav-item">
        <Link to={this.props.match.params.lang + '/' }><div className ='nav-link'> Home</div></Link>
      </li>
      <li className="nav-item">
        <Link to={this.props.match.params.lang + '/product_list/' }><div className='nav-link'>Product List</div></Link>
      </li>
      <li className="nav-item">
        <Link to={this.props.match.params.lang + '/contact/' } ><div className="nav-link">Contact</div></Link>
      </li>
      <li className="nav-item">
        <Link to={this.props.match.params.lang + '/animation/' }><div className="nav-link">Animation</div></Link>
      </li> 
     
      {this.state.user !== 'False' ?
       <React.Fragment>
        <li className="nav-item">
          <Link to={this.url()}><div className="nav-link">Create an Announce</div></Link>
        </li> 
        <li className="nav-item">
          <Link to={this.url2}><div className="nav-link">Your Announcement</div></Link>
        </li>
       </React.Fragment>
       : ''}
     {this.state.user !== 'False' ? <React.Fragment>
        <li className="nav-item">
          <Link to={this.props.match.params.lang + '/cart/' } ><div className="nav-link">Cart <div className="badge badge-info"> {this.props.state.cart} </div></div></Link>
        </li>
        <li className="nav-item">
          <Link to= {this.props.match.params.lang + '/wishlist/' } ><div className="nav-link">Wishlist <div className="badge badge-info">{this.props.state.wishlist}</div> </div></Link>
        </li></React.Fragment>
       : ''}
    </ul>
    <div className="form-inline my-2 my-lg-0">
      <div className='dropdown' style={{marginRight:'70px'}}>  
         <button class=" btn btn-light dropdown-toggle" href="#" id="navbarDropdown" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
           {this.props.state.lang} 
        </button>
        <div className="dropdown-menu" aria-labelledby="navbarDropdown">
          <div class="dropdown-item" onClick={this.props.en}>English</div>
          <div class="dropdown-item" onClick={this.props.ru}>Russian</div>
          <div class="dropdown-item" onClick={this.props.uz}>Uzbek</div>
        </div>   
      </div>
    </div>
    <div className="form-inline my-2 my-lg-0">
          <br/>
     <div>{this.authenticated()}</div>
     
    </div>
   
  </div>  
</nav>
        </div>);
    }
}
 
export default Navbar;
