import React, { Component } from 'react';
import Navbar from './compenents/Navbar'
import axios from 'axios'
import Detail1 from './compenents/detail1'

class Detail extends Component {
    constructor(props){
        super(props);
        this.state = {
            slug:null,
            array:[]
        }
        this.componentDidUpdate = this.componentDidUpdate.bind(this);
    }
    componentDidMount()
    {   
        
        let comp = this;
        console.log(this,'MOUnt')
        comp.setState({
            slug:null,
        })
        if(comp.props.match)
        {
           const slug = comp.props.match.params.slug
           comp.setState({
               slug:slug
           })
           let url = "/detail/" + slug;
           axios.get(url)
           .then(response=>{
            comp.setState({
                array:response.data
            }) 
            console.log(comp.state.array)
           })
           
           .catch(errors =>{
               console.log(errors)
           })
        }
        else{
            console.log("There is not any slug")
        }
    }
    componentDidUpdate(){
        let comp = this;
        setInterval(() => {
           if(this.props.match.params)
           {
            let comp = this;
            const slug = comp.props.match.params.slug
            comp.setState({
               slug:slug
           })
            let url = "/detail/" + slug;
          fetch(url)
          .then(response=>{
            comp.setState({
                array:response.data
            }) 
            console.log(comp.state.array)
           })
           }
        }, 1000);
      }
    render() {
        return (
        <div>
            <Navbar />
            <Detail1 pro={this.state.array}/> 
        </div>
        );
    }
}
 
export default Detail;
