import axios from 'axios';
import React, { Component } from 'react';
import {
    Link
  } from "react-router-dom";
import Navbar from './compenents/Navbar';
class Profile extends Component {
    constructor(props){
        super(props);
        this.lang2 = this.lang2.bind(this);
    }
    state = {
        post:[],
        success:'',
        wishlist:0,
        cart:0,
        lang:''
    }
    lang2(ev){
        localStorage.setItem('lang',ev);
        this.setState({
            lang:ev
        })
    
      }
    componentDidMount(){
        axios.get('http://127.0.0.1:8000/rtqkaq2345akdker24/')
        .then(response =>{
            console.log(response)
            this.setState({
                post:response.data
            })
        })
        .catch(errors =>{
            this.setState({
                success:'Something went wrong!!'
            })
        })
        axios.get('http://127.0.0.1:8000/sdlkewrws/')
        .then(response => {
            this.setState({
            cart:response.data.cart,
            wishlist:response.data.wishlist
            })
        })
    }
    render() { 
        console.log(this.state.post)
        return ( 
               <div>
                   <Navbar state = {this.state} lang2={this.lang2} />
                    <div className='container'>
                    <h1>My Profile</h1><br/><br/>
                       <h3>Name:{this.state.post.first_name}</h3><br/>
                       <h3>Surname: {this.state.post.last_name}</h3><br/>
                       <h3>Username: {this.state.post.username}</h3><br/>
                       <h3>Email:{this.state.post.email}</h3><br/>
                       <Link to="/edit/"><div className="btn btn-lg btn-info">Edit</div></Link>
                </div>
               </div>
                );
    }
}
 
export default Profile;
