import ContactPage from './contact'
import List from './list'
import cookie from 'react-cookies'
import 'whatwg-fetch'
import React, { Component,useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import PostInline from './compenents/post'
import Contact from './compenents/contact'
import Navbar from './compenents/Navbar'
import Detail from './product detail'
import {BrowserRouter,Route,Redirect,Switch} from 'react-router-dom'
import notfound from './404'
import CreateProduct from './compenents/createproduct';
import Login from './login'
import { Spring,useSpring,animated } from 'react-spring'
import Register from './register';
import Announce_post from './compenents/announce_part';
import Announcements from './announcements';
import UpdateProduct from './updateproduct';
import Wishlist from './wishlist';
import axios from 'axios'
import Cart from './cart'
import Profile from './profile';
import ProfileForm from './compenents/profile_form';
import { GoogleApiWrapper } from 'google-maps-react';
import App from './compenents/animation';
import Api from './compenents/map';
import {I18nProvider,LOCALES} from './i18n'
import translate from './i18n/tranlate'

function Animation1() {
    const props = useSpring({ to: { opacity: 1 }, from: { opacity: 0 } })
    return ( 
    <animated.div style={props}> <div style={{width:'300px',wordBreak:'break-all',padding:'10px',backgroundColor:'lavender',borderRadius:'10px'}}> Lorem ipsum dolor sit amet consectetur adipisicing elit. Voluptatum reprehenderit labore quaerat minus consectetur, optio fugiat dolore, amet ipsam quos libero? Maxime autem veritatis praesentium mollitia! Repudiandae voluptates omnis vitae! </div></animated.div>
    );
}

class Home extends Component {
    state={
        cart:0,
        wishlist:0,
        lang:'en'
    }
    constructor(props){
        super(props);
        this.en = this.en.bind(this);
        this.ru = this.ru.bind(this);
        this.uz = this.uz.bind(this);
        this.lang1 = this.lang1.bind(this)
    }
    
    componentDidMount(){
        axios.get('/sdlkewrws/')
        .then(response => {
        this.setState({
            cart:response.data.cart,
            wishlist:response.data.wishlist
        })
        })
    }

    en(){
        this.setState({
            lang:'en'
        })
    }
    ru(){
        this.setState({
            lang:'ru'
        })
    }
    uz(){
        this.setState({
            lang:'uz'
        })
    }
    lang1(){
        console.log("Lang1")
        if(this.state.lang === 'en'){
            return LOCALES.English
        }
        else if(this.state.lang === 'ru'){
            return LOCALES.Russian 
        }
        else if(this.state.lang === 'uz'){
            return LOCALES.Uzbek
        }
        else{
            console.log("Error")
        }
    }

    render() { 
        console.log(this.state.lang,this.props.match)
        return (
                <BrowserRouter>
                <Switch>
                    <Route exact path = "/:lang/"> <I18nProvider locale = {this.lang1()}> <Navbar state={this.state} ru = {this.ru} uz={this.uz} en = {this.en} /> 
                    {translate("hello")}<br/> {translate('greeting',{path: <strong>Abduqodir</strong> })}
                  </I18nProvider>  </Route>
                    <Route path = "/:lang/product_list" component={List} />
                    <Route path = "/:lang/contact/:lang" component={Contact} />
                    <Route path = "/:lang/product/detail/:slug" component={Detail} />
                    <Route path = "/:lang/notfound" component={notfound} />
                    <Route path = '/:lang/create/product' component={CreateProduct} />
                    <Route path = '/:lang/login' component={Login} />
                    <Route path = '/:lang/signup' component={Register} />
                    <Route path = '/:lang/announcement' component={Announcements} />
                    <Route path = '/:lang/update/:id' component={UpdateProduct} />
                    <Route path = '/:lang/wishlist/' component={Wishlist} />
                    <Route path = '/:lang/cart/' component={Cart} />
                    <Route path = '/:lang/profile' component={Profile} />
                    <Route path = '/:lang/edit/' component={ProfileForm} />
                    <Route path = '/:lang/map/' component={Api} />
                    <Route path = '/:lang/animation/' component={App} />
               
                </Switch>

      </BrowserRouter>
        );
    }
}
 
export default Home;
