import React, { Component } from 'react';
import Navbar from './compenents/Navbar'
import axios from 'axios'
import Detail1 from './compenents/detail1'
import {Redirect} from 'react-router-dom'
import { I18nProvider, LOCALES } from './i18n';

class Detail extends Component {
    constructor(props){
        super(props);
        this.lang2=this.lang2.bind(this)
    }
    state = {
        slug:null,
        valid:true,
        array:[],
        cart:0,
        wishlist:0,
        lang:''
    }
    lang2(ev){
        localStorage.setItem('lang',ev);
        this.setState({
            lang:ev
        })
    
      }
    componentDidMount()
    {   
        let comp = this;
        if(comp.props.match)
        {
           const slug = comp.props.match.params.slug
           comp.setState({
               slug:slug
           })
           let url = "http://127.0.0.1:8000/detail/" + slug;
           axios.get(url)
            .then(response=>{
                console.log(response)
                comp.setState({  
                    array:response.data
                })
            })
            .catch(errors =>{
                console.log('Error')
                console.log(errors);
                comp.setState({
                    valid:false
                });
            })
        }
        else{
            console.log("There is not any slug")
        }
        axios.get('http://127.0.0.1:8000/sdlkewrws/')
        .then(response => {
          this.setState({
            cart:response.data.cart,
            wishlist:response.data.wishlist
          })
        })
    }
    

    
    valid = () => {
        console.log(this)
        if(this.state.valid){
            return <Detail1 pro={this.state.array}/>;
        }
        else if (this.state.valid !== true){
            return <Redirect to='/notfound' />;
        }
    }
    lang1(){
        if(localStorage.getItem('lang') === 'en'){
            
            return LOCALES.English
        }
        else if(localStorage.getItem('lang')=== 'ru'){
            return LOCALES.Russian 
        }
        else if(localStorage.getItem('lang')=== 'uz'){
            return LOCALES.Uzbek
        }
        else{
            console.log("Error")
        }
    }
    render() {
        return (
        <I18nProvider locale={this.lang1()}>
            <div>
      
            {this.valid()}
        </div>
        </I18nProvider>
        );
    }
}
 
export default Detail;
