import React, { Component,useEffect } from 'react';
import { Spring,useSpring,animated } from 'react-spring'
import Aos from 'aos'
import 'aos/dist/aos.css'
import 'aos/dist/aos.js'
const App = () => {
    useEffect(() =>{
        Aos.init({duration:2000})
    })
    
    const props1 = useSpring({
        from:{
            opacity:0,
            marginTop:-500
        },
        to:{
            opacity:1,
            marginTop:0
        }
    })
    const props2 = useSpring({
        from:{
            opacity:0,
            marginLeft:-500
        },
        to:{
            opacity:1,
            marginLeft:60
        },
       
    })
    const props3 = useSpring({
        from:{
            opaxity:0,
            width:'0%'
        },
        to:{
            opacity:1,
            width:'70%'
        },
       
    })
    return  (
                <div>
                    <div  class="progress" style={{padding:'50px'}}>
                        <animated.div style={props3} class="progress-bar progress-bar-striped"></animated.div>
                    </div>
                    <animated.div data-aos='fade-left' className='container p-3'>
