import React, { Component } from 'react';
import axios from 'axios'
import cookie from 'react-cookies'
import {
    Link
  } from "react-router-dom";
class PostInline extends Component {
   constructor(props){
       super(props)
       this.href = this.href.bind(this)
       this.cart = this.cart.bind(this)
   }
   state = {
       success:''
   }
    sale = () =>{
        if(this.props.post.sale)
        {
            return <div>{this.props.post.price_in_sale} So'm</div>
        }
        else{
            return <div>{this.props.post.price} So'm</div>
        }
    }
    href(){
        return "/product/detail/" + this.props.post.id;
    }
    cart(){
        let csrf = cookie.load('csrftoken');
        let id = this.props.post.id
        axios({
            method:'POST',
            url:'/sadfasdfgjkljk/',
            headers:{
                'Content-type':'application/json',
                'X-CSRFToken': csrf
            },
            data:{'id': id},
            credentials:'include'
        })
        .then(response =>{
            if(response.data === 'OK')
            {
                this.setState({
                    success:"Product is added to your cart successfully"
                })
                setTimeout(() => {
                    this.setState({
                        success:''
                    })
                }, 5000);
            }
            else{
                this.setState({
                    success:'Product could not be added to your cart!!'
                })
                setTimeout(() => {
                    this.setState({
                        success:''
                    })
                }, 5000);
            
            }
        })
    }
    render() { 
        return (
 
               
                <div className = 'col-md-4' style={{overflow:'hidden',borderRadius:'15px'}}>
                     {this.state.success !== '' ?   <center><div className='alert alert-success fixed-top'>{this.state.success}</div></center> : ''}
                    <div className="card" style={{width:'100%'}}>
                        <img className="card-img-top" src={this.props.post.img} style={{height:300}} />
                        <div className="card-body">
                        <Link to={this.href}>
                                <h4 className="card-title"  style={{height:'60px',overflow:'hidden',wordBreak:'break-all'}}>{this.props.post.name}</h4>
                        </Link>
                        <p className="card-text"><strong>{this.sale()}</strong></p>
                         <div className="row"> <div className="col-md-2"></div> <div onClick={this.cart} className="col-md-2" style={{borderRadius:'15px',boxShadow:'1px 1px 2px 2px lavender'}}><i className="fas fa-cart-plus"></i></div> <div className="col-md-1"></div> <div className="col-md-2" style={{borderRadius:'10px',boxShadow:'1px 1px 2px 2px lavender'}} ><i className="fas fa-heart"></i>
                        </div> <div className="col-md-2"></div> </div> <br/>
                        <a  className="btn btn-primary">Buy Now</a>
                        </div>
                    </div><br/>
            
        </div> 
        
        );
    }
}
 
export default PostInline;
