import React, { Component } from 'react';

class CheckoutForm extends Component {
    constructor(props){
        super(props);
        this.state = this.props.state
        this.handleform = this.handleform.bind(this);
    }
    handleform(event){
        this.setState({
            [event.target.name]:event.target.value
        })
    }
    render() { 
        console.log(this.state)
        return (
            <div className='container'>
                <label htmlFor="address">Your Address:</label>
                <input type="text" id='address' name='address' placeholder='Address where Product is delivered' className='' />
                <label htmlFor="tel_num">Your Telephone Number:</label>
                <input type="number" id='tel_num' name="tel_num" placeholder='Your telephone number to connect with you' />
                
            </div>
        );
    }
}
 
export default CheckoutForm;
