import React, { Component } from 'react';
import axios from 'axios'
import cookie from 'react-cookies'
import Navbar from './Navbar'
import { Redirect } from 'react-router';
class Contact extends Component {
    constructor(props)
    {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handlefield = this.handlefield.bind(this);
        this.data1 = this.data1.bind(this);
        this.clearform = this.clearform.bind(this);
        this.clearform = this.clearform.bind(this)
    }
    state = {
        name : null,
        number:null,
        msg:null,
        success:'',
        errors:null,
        csrfmiddlewaretoken:cookie.load('csrftoken'),
        cart:0,
        wishlist:0
    }
    data1(data){
        let csrf = cookie.load('csrftoken');
        console.log(data)
            return <div style={{color:'red'}}>
                        {this.state.success}
          </div>
    }
    componentDidMount(){
        axios.get('http://127.0.0.1:8000/sdlkewrws/')
        .then(response => {
            this.setState({
            cart:response.data.cart,
            wishlist:response.data.wishlist
            })
        })
      }    
    empty = () =>{
        setTimeout(() => {
            this.setState({
                success:''
            })
        }, 9000);
    }
    clearform(){
        this.PostCreateForm.reset();
    }
    handleSubmit(event){
        console.log(this.state)
        let comp = this
        let csrf = cookie.load('csrftoken');
        event.preventDefault();
        axios({
            method:'POST',
            url:'http://127.0.0.1:8000/contact1/',
            headers:{
                'Content-Type':'application/json',
                'X-CSRFToken':csrf
            },
            data:this.state,
            credentials:'include'
        })
        .then(response => {
            comp.setState({
                success:response.data
            })
            this.data1(response.data)
            
        })
        .catch(errors => {
            comp.setState({
                success:"Something Went Wrong!"
            })
            console.log(errors)
        })
        console.log('Handle Submit');
        this.clearform()
    }
    handlefield(event)
    {
        event.preventDefault();
        this.setState({
            [event.target.name]:event.target.value,
        })
        
    }
    render() {
            return ( 
            
                <div>
                    <Navbar state={this.state} /> <br/>
                 
                 <div>{this.data1()}{this.empty()}   </div>
                 <form onSubmit={this.handleSubmit} ref = {(el) => this.PostCreateForm = el} className="container">
                 <h2>Contact Page</h2><br/>
                     <label> Ismingiz</label>
                          <input className='form-control' type="text" name="name" id="" placeholder='Sizning Ismingiz' onChange={this.handlefield} required = 'required' />
                     <br/>
                     <label> Telefon Nomer</label>
                          <input className='form-control' type="number" name="number" placeholder='Sizning Raqamingiz' onChange={this.handlefield} required = 'required' />
                     <br/>
                     <label> Sizning Xabaringiz</label> <br/>
                          <textarea className='form-control' name="msg" cols="50" rows="10" onChange={this.handlefield} required = 'required'></textarea>
                      
                          <br/>
                          
                     <button type='submit' className='btn btn-info'>Submit</button>
                     
                 </form>
             </div> 
              );
              
             
      

    }
}
 
export default Contact ;
