import React, { Component } from 'react';

class CreateProduct extends Component {
    constructor(props){
        super(props);
        this.state = {
            name:null,
            color:null,
            desc:null,
            price:null,
            sale:null,
            price_in_sale:null,
            quantity:null,
            img:undefined,
            size:null,
            date:undefined,
            category:null,
            brand:null,
        }
        this.handleForm = this.handleForm.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleForm(event) {
        event.preventDefault();
        this.setState({
            [event.target.name]:event.target.value,
        })   
    }
    price = () =>{
        if(this.state.sale){
            console.log(this.state.sale)
            return (<div><label htmlFor="price_in_sale">Price in Sale</label> <input type="number" name="price_in_sale" className='form-control' onChange={this.handleForm} /></div>)
        }
        else{
            return (<div><label htmlFor="price">Price</label><input type="number" name="price" className='form-control' onChange={this.handleForm} /></div>);
        }
    }
    handleSubmit(event){
        event.preventDefault();
    }
   
    render() { 
        return ( 
            <div className='container'>
               <form  onSubmit={this.handleSubmit} className='container'>
                    <label htmlFor='name'>Product Name</label>
                    <input type="text" value={this.state.name} name="name" className='form-control' onChange={this.handleForm} required='required' />
                    <label htmlFor="quantity">Quantity</label>
                    <input type="number" value={this.state.quantity} className='form-control' name="quantiy" required='required' onChange={this.handleForm}/>
                    <label htmlFor="sale">Sale </label>
                    <input type="checkbox" name='sale' value = {this.state.sale} required='required' onChange={this.handleForm} />
                    {this.price()}
                    <label htmlFor="img">Main Image of Product</label>
                    <div class="custom-file mb-3">
                    <input type="file" class="custom-file-input" id="customFile" name="img" />
                    <label class="custom-file-label" for="customFile">Choose File</label>
                    </div>
                </form>
            </div>
         );
    }
}
 
export default CreateProduct;
