import axios from 'axios';
import React, { Component } from 'react';
import Navbar from './compenents/Navbar';
import Product_detail from './compenents/wishlist_component';

class Cart extends Component {
    constructor(props){
        super(props)
        this.refresh = this.refresh.bind(this)
        this.count = this.count.bind(this)
    }
    state = {
        posts:[],
        success:'',
        cart:0,
        wishlist:0
    }
    componentDidMount(){
        axios.get('/32234jnk234324/')
        .then(response =>{
            console.log(response.data,"Response")
           this.setState({
               posts:response.data
           })
        })
        axios.get('/sdlkewrws/')
    .then(response => {
      this.setState({
        cart:response.data.cart,
        wishlist:response.data.wishlist
      })
    })
    }
    count(){
        axios.get('/sdlkewrws/')
        .then(response => {
        this.setState({
            cart:response.data.cart,
            wishlist:response.data.wishlist
        })
        })
    }
    refresh(){
        axios.get('/32234jnk234324/')
        .then(response =>{
            console.log(response.data,"Response")
           this.setState({
               posts:response.data
           })
        })
    }
    render() { 
        return (
            <div>
                <Navbar state={this.state} />
                <br/>

                <div className='container'>
                <h1>Cart</h1>
                <br/>
                <br/>
               {this.state.posts.length > 0 ?
                <React.Fragment>
                     <table class="table table-hover">
                    <tbody>
                    <tr>
                        <td>Product Name</td>
                        <td>Quantity</td>
                        <td>Total Price</td>
                        <td>Action</td>
                    </tr>
                    {this.state.posts.map(product => <Product_detail key = {product.id} count = {this.count} product = {product} state = {this.state} mount = {this.refresh} />)}
                    </tbody>
                </table>
               </React.Fragment>:  <center className='alert alert-danger'><h1>There is nothing in your cart</h1></center> }
              
                </div>
            </div>
        );
    }
}
 
export default Wishlist;
