import React, { Component } from 'react';
import Navbar from './compenents/Navbar'

class Detail extends Component {
    constructor(props){
        super(props);
        this.state = {
            slug:null
        }
    }
    componentDidMount()
    {
        let comp = this;
        console.log(this,'MOUnt')
        comp.setState({
            slug:null,
        })
        if(comp.props.match)
        {
           const slug = comp.match.params.slug
           comp.setState({
               slug:slug
           })
           console.log(slug)
        }
        else{
            console.log("There is not any slug")
        }
    }
    render() { 
        return (
        <div>
            <Navbar />
            <h2>{this.state.slug}</h2>
        </div>
        );
    }
}
 
export default Detail;
