import axios from 'axios';
import React, { Component } from 'react';
import Navbar from './compenents/Navbar';
import Product_detail from './compenents/cart_components';
import Loading from './compenents/loading';
import CheckoutForm from './compenents/checkout_form';

class Cart extends Component {
    constructor(props){
        super(props);
        this.refresh = this.refresh.bind(this);
        this.count = this.count.bind(this);
        this.ch = this.ch.bind(this);
        this.handleform = this.handleform.bind(this);
        this.handlesubmit = this.handlesubmit.bind(this);
        }
    state = {
        posts:[],
        success:'',
        cart:0,
        wishlist:0,
        isloading:false,
        checkout:false,
        address:null,
        tel_num:null,
        name:null,
        surname:null
    }
    componentDidMount(){
        axios.get('/32234jnk234324/')
        .then(response =>{
            console.log(response.data,"Response")
           this.setState({
               posts:response.data,

           })
        })
        axios.get('/sdlkewrws/')
    .then(response => {
      this.setState({
        cart:response.data.cart,
        wishlist:response.data.wishlist,
        isloading:true
      })
    })
    }
    count(){
        axios.get('/sdlkewrws/')
        .then(response => {
        this.setState({
            cart:response.data.cart,
            wishlist:response.data.wishlist,
            isloading:true
        })
        })
    }
    refresh(){
        axios.get('/32234jnk234324/')
        .then(response =>{
            console.log(response.data,"Response")
           this.setState({
               posts:response.data
           })
        })
    }
    ch(){
        this.setState({checkout:!this.state.checkout})
    }
    handleform(event){
        event.preventDefault()
        this.setState({
            [event.target.name]:event.target.value
        })
    }
    handlesubmit(event){
        event.preventDefault()
        let form = new Form()
        let csrf = cookie.load('csrftoken')
        form.append('address',this.state.address);
        form.append('tel_num',this.state.tel_num);
        form.append('name',this.state.name);
        form.append('surname',this.state.surname);
        axios({
            method:POST,
            url:'/sefll123123lqmq2334412/',
            headers:{
                'content-type':'application/json',
                'X-CSRFToken': csrf
            },
            data:form,
            credentials:'include'
            
        })
        .then(response =>{
            console.log(response)
        })
        console.log(this)
    }
    render() { 
        console.log(this.state.checkout)
        if(this.state.isloading)
        {
            return (
                <div>
                    <Navbar state={this.state} />
                    <br/>
    
                    <div className='container'>
                    <h1>Cart</h1>
                    <br/>
                    <br/>
                   {
                    this.state.posts.length > 0 ?
                        <React.Fragment>
                        <table class="table table-hover">
                            <tbody>
                                <tr>
                                    <td>Product Name</td>
                                    <td>Quantity</td>
                                    <td>Total Price</td>
                                    <td>Action</td>
                                </tr>
                                {this.state.posts.map(product => <Product_detail key = {product.id} count = {this.count} product = {product} state = {this.state} mount = {this.refresh} />)}
                            </tbody>
                        </table>
                    </React.Fragment>:  <center className='alert alert-danger'><h1>There is nothing in your cart</h1></center>
                   }
                    <button onClick={this.ch} className='btn btn-info' type='button'>Checkout</button>
                    {this.state.checkout ?  <div className='container'>
                <br/>
               <form onSubmit={this.handlesubmit}>

                    <label htmlFor="name">Your Name:</label>
                    <input type="text" id='name' name='name' placeholder='Your Name' className='form-control' onChange = {this.handleform} />
                    <label htmlFor="surname">Your Surname:</label>
                    <input type="text" id='surname' name='surname' placeholder='Your Surname' className='form-control' onChange = {this.handleform} />
                    <label htmlFor="address">Your Address:</label>
                    <input type="text" id='address' name='address' placeholder='Address where Product is delivered' className='form-control' onChange = {this.handleform} />
                    <label htmlFor="tel_num">Your Telephone Number:</label>
                    <input type="number" className='form-control' id='tel_num' name="tel_num" placeholder='Your telephone number to connect with you' onChange = {this.handleform} />
                    <br/>
                    <input type="submit" value="Submit" className='btn btn-info'/>
                    <br/><br/>
               </form>
            </div> : ''}
                    </div>

                </div>
            );
        }
        else{
            return (<Loading />);
        }
    }
}
 
export default Cart;
