import React, { Component } from 'react';

class CreateProduct extends Component {
    constructor(props){
        super(props);
        this.state = {
            name:null,
            color:null,
            desc:null,
            price:null,
            sale:null,
            price_in_sale:null,
            quantity:null,
            img:undefined,
            size:null,
            date:undefined,
            category:null,
            brand:null,
        }
        this.handleForm = this.handleForm.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleForm(event) {
        event.preventDefault();
        console.log(event.target.name,event.target.value)
        this.setState({
            [event.target.name]:event.target.value,
        })   
    }

    handleSubmit(event){
        event.preventDefault();
        console.log(this.state)
    }
   
    render() { 
        return ( 
            <div className='container'>
               <form  onSubmit={this.handleSubmit} className='container'>
                    <label htmlFor='name'>Product Name</label>
                    <input type="text" value={this.state.name} name="name" className='form-control' onChange={this.handleForm} required='required' />
                    <label htmlFor="quantity">Quantity</label>
                    <input type="number" value={this.state.quantity} className='form-control' name="quantity" required='required' onChange={this.handleForm}/>
                    <label htmlFor="price">Price</label><input type="number" name="price" className='form-control' onChange={this.handleForm} />
                    <label htmlFor="img">Main Image of Product</label>
                    <div class="custom-file">
                        <input type="file" class="custom-file-input" id="customFile" name='img' />
                        <label class="custom-file-label" for="customFile"></label>
                    </div>
                    <label htmlFor="size">Available sizes</label>
                    <input type="text"  name="size" className='form-control' onChange={this.handleForm} />
                    <label htmlFor="color">Color of Product</label>
                    <input type="text"  name="color" className='form-control' onChange={this.handleForm} />
                    <label htmlFor="desc">Full Description</label>
                    <textarea  cols="30" rows="10" name="desc" className='form-control' onChange={this.handleForm}></textarea>
                    <label htmlFor="category">Category</label>
                    <select name="category" class="custom-select mb-3" onChange={this.handleForm}>
                        <option selected>-------------------</option>
                        <option value="Fashion & Beauty">Fashion & Beauty</option>
                        <option value="Kids & Babies Clothes">Kids & Babies Clothes</option>
                        <option value="Men & Women  Clothes">Men & Women  Clothes</option>
                        <option value="Gadgets & Accessories">Gadgets & Accessories</option>
                        <option value="Electronics & Accessories">Electronics & Accessories</option>
                    </select>
                    <label htmlFor="brand">Brand</label>
                    <input type="text" name="brand" className='form-control' onChange={this.handleForm}  />
                   <br/><br/> <input type="submit" className="btn btn-info" />

                </form>
            </div>
         );
    }
}
 
export default CreateProduct;
