import logo from './logo.svg';
import './App.css';
import cookie from 'react-cookies'
import 'whatwg-fetch'
import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import PostInline from './compenents/post'

class App  extends Component {
  state = {
    posts:[]
  }
  getdata(){
    let post = []
    console.log(this)
      let lookup = {
          method:"GET",
          headers:{
            'content-type':'application/json'
            } 
        }
    fetch('http://127.0.0.1:8000/api1',lookup)
    .then(function(response){
      return response.json();
    }).then(function(posts){
        console.log(posts);
        post = posts
    }).catch(function(error){
      console.log('error',error);
    })
    this.setState({
      posts:post
    })
    console.log(post)
  }
componentDidMount()
  {
      this.setState({
        posts:[]
      });
      this.getdata();
  }


  render()
  {
    const {posts} = this.state
    return (
      <div>
        Hello World
        {this.state.posts.map(post => <PostInline key = {post.id} post = {post}/>)}
      </div>
    );
  }
}


export default App;
