import React, { Component } from 'react';
import axios from 'axios'
import cookie from 'react-cookies'
import Navbar from './compenents/Navbar'
import { Redirect } from 'react-router';
class Register extends Component {

    constructor(props)
    {
        super(props);
  
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handlefield = this.handlefield.bind(this);
        this.data1 = this.data1.bind(this);
        this.clearform = this.clearform.bind(this);
    }
    state = {
        username : null,
        username_errror:null,
        email:null,
        email_error:null,
        password1:null,
        password1_error:null,
        password2:null,
        password2_error:null,
        success:'',
        valid:false
    }
    data1(data){
        let csrf = cookie.load('csrftoken');
            return <div style={{color:'red'}}>
                        {this.state.success}
          </div>
    }
            
    empty = () =>{
        setTimeout(() => {
            this.setState({
                success:''
            })
        }, 9000);
    }
    clearform(){
        this.PostCreateForm.reset();
    }
    handleSubmit(event){
        event.preventDefault();
        let comp = this;
        var formData = new FormData()
        formData.append('username',this.state.username);
        formData.append('email',this.state.email);
        formData.append('password1',this.state.password1);
        formData.append('password2',this.state.password2);
        let csrf = cookie.load('csrftoken');
        axios({
            method:'POST',
            url:'/register/',
            headers:{
                'Content-Type':'application/json',
                'X-CSRFToken':csrf
            },
            data:formData,
            credentials:'include'
        })
        .then(response => {
                if(response.data === 'True'){
                    comp.setState({
                        valid:true,
                        success:'New Account is created succesfully'
                    })
                }
                else if(response.data.username){
                    comp.setState({
                        username_errror:response.data.username
                    })
                }
                else if(response.data.email){
                    comp.setState({
                        email_error:response.data.email
                    })
                }
                else if(response.data.password1){
                    comp.setState({
                        password1_errror:response.data.password1
                    })
                }
                else if(response.data.password2){
                    comp.setState({
                        password2_errror:response.data.password2
                    })
                }
                
                
           
            
        })
        this.clearform()
    }
    handlefield(event)
    {
        event.preventDefault();
        this.setState({
            [event.target.name]:event.target.value,
        })
        
    }
    
    render() {
        let csrf = cookie.load('csrftoken');
        console.log(this.state.success,"Salom")
        if(csrf !== null && csrf !== undefined){
               if(this.state.valid)
               {
                return <Redirect to='/login'/>
               }
               else{
                return ( 
            
                    <div>
                       <Navbar />
                     <div className='container'>{this.data1()}{this.empty()}   </div>
                     <form onSubmit={this.handleSubmit} ref = {(el) => this.PostCreateForm = el} className="container">
                     <h2> Register Page</h2><br/>
                         <label> Foydalanuvchi nomi:</label>
                              <input className='form-control' type="text" name="username" id="" placeholder='Sizning Foydalanuvchi nomingiz' onChange={this.handlefield} required = 'required' />
                        <br/>
                        <label style={{color:'red'}}>{this.state.email_error}</label>
                         <br/>
                         <label htmlFor="email">Sizning Elektrion pochtangiz</label>
                            <input type="email" name="email" className='form-control' placeholder='Siznging elektron pochtangiz' onChange={this.handlefield} required='required' />
                           <br/> <label style={{color:'red'}}>{this.state.email_error}</label><br/>
                         <label>Parol:</label>
                              <input className='form-control' type="password" name="password1" placeholder='Password' onChange={this.handlefield} required = 'required' />
                              <br/> <label style={{color:'red'}}>{this.state.password1_error}</label>
                         <br/><label>Tasdiqlash Parol:</label>
                              <input className='form-control' type="password" name="password2" placeholder='Confirmation Password' onChange={this.handlefield} required = 'required' />
                              <br/> <label style={{color:'red'}}>{this.state.password2_error}</label><br/>
                         <br/>
                              <br/>
                              
                         <button type='submit' className='btn btn-info'>Submit</button>
                         
                     </form>
                 </div> 
                  );
               }
             
        }
        else{
            return <Redirect to='/notfound'/>
        }

    }
}
 
export default Register ;
