import logo from './logo.svg';
import './App.css';
import cookie from 'react-cookies'
import 'whatwg-fetch'
import React, { Component } from 'react';
import PostInline from './compenents/post'

class App  extends Component {
  state = {
    posts:[],
  }
  componentDidMount()
  {
    let post = this.state.posts
    const csrf = cookie.load('csrfToken');
    let data = {
      'name':'',
      'price':undefined,
      'quantity':undefined
    }
    if(csrf!= undefined)
    {
      let lookup = {
        method:"POST",
        headers:{
          'content-type':'application/json',
          'X-CSRFToken':csrf
        },
        body:JSON.stringify(data),
        Credentials:'include'
      }
    }

      let lookup = {
          method:"GET",
          headers:{
            'content-type':'application/json'
            } 
        }

    fetch('api1',lookup)
    .then(function(response){
      return response.json();
    }).then(function(resposeData){
        this.state.setState({
          post:resposeData
        })
    }).catch(function(error){
      console.log('error',error)
    })
  }
  render()
  {
    return (
      <div>
        {this.state.posts.map(post => <PostInline key = {post.id} post = {post}/>)}
      </div>
    );
  }
}


export default App;
