import React, { Component } from 'react';

class Detail1 extends Component {
    sale = () =>{
        if(this.props.pro.sale)
        {
            return <div><strong>Price:</strong>{this.props.pro.price_in_sale} So'm</div>
        }
        else{
            return <div> <strong>Price:</strong> {this.props.pro.price} So'm</div>
        }
    }
    render() { 
        return ( 
        <main>
            <br/>
            <br/>
           <div className="container">
               <div className="row">
                   <div className="col-md-5">
                   <div id="demo" className="carousel slide" data-ride="carousel">
                        <ul className="carousel-indicators">
                            <li data-target="#demo" data-slide-to="0" className="active"></li>
                        </ul>
                        <div className="carousel-inner">
                            <div className="carousel-item active">
                            <img src={this.props.pro.img} alt="Los Angeles" style={{width:'100%',height:'100%'}}/>
                         
                            </div>
                        </div>
                        <a className="carousel-control-prev" href="#demo" data-slide="prev">
                            <span className="carousel-control-prev-icon"></span>
                        </a>
                        <a className="carousel-control-next" href="#demo" data-slide="next">
                            <span className="carousel-control-next-icon"></span>
                        </a>
                        </div>
                   </div>
                   <div className="col-md-7">
                       <h1> <strong>Name:</strong> {this.props.pro.name}</h1>
                        {this.sale()} 
                       <div><strong>Brand:</strong>{this.props.pro.brand}</div>
                       <div><strong>Color:</strong>{this.props.pro.color}</div>

                   </div>
               </div>
               <br/><br/>
               <div>
                  <strong>Full Description:</strong>{this.props.pro.desc}
               </div><br/><br/>
           </div>
           
        </main> 
        );
    }
}
 
export default Detail1;
