import cookie from 'react-cookies'
import 'whatwg-fetch'
import React, { Component,useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import PostInline from './compenents/post'
import Contact from './compenents/contact'
import {BrowserRouter,Route,Redirect,Switch} from 'react-router-dom'
import Navbar from './compenents/Navbar';
import Loading from './compenents/loading';
import axios from 'axios'

class List extends Component {
  constructor(props){
    super(props);
    this.count = this.count.bind(this)
    this.lang2 = this.lang2.bind(this)
  }
    state = {
      loading:true,
        posts:[],
        cart:0,
        wishlist:0,
        lang:''
      }
      componentDidMount()
      {
        this.setState({
          posts:[],
        })
        fetch('http://127.0.0.1:8000/api1/')
        .then((response) => response.json())
        .then(booksList => {
         if(this.state.posts != booksList)
         {
           console.log(booksList)
          this.setState({ posts: booksList,loading:false});
         }  
        });
        axios.get('http://127.0.0.1:8000/sdlkewrws/')
        .then(response => {
          this.setState({
            cart:response.data.cart,
            wishlist:response.data.wishlist
          })
        })
      }
      lang2(ev){
        localStorage.setItem('lang',ev);
        this.setState({
            lang:ev
        })
    
      }
      count(){
        axios.get('http://127.0.0.1:8000/sdlkewrws/')
        .then(response => {
        this.setState({
            cart:response.data.cart,
            wishlist:response.data.wishlist
        })
        })
    }
    render() { 
        if(this.state.loading){
          return <Loading />
        }
        else{
          return ( 
            <div>
                <Navbar state={this.state} lang2={this.lang2} /><br/>
                <div className="container">
                    <h3>Product List </h3><br/>
                    <div className='container'><div className='row'>{this.state.posts.map(post => <PostInline key = {post.id} count={this.count} post = {post} /> )}</div></div> 
                </div>
            </div>
         );
        }
    }
}
 
export default List;
