import React, { Component,useEffect } from 'react';
import { Spring,useSpring,animated,Transition,useTransition } from 'react-spring'
import Aos from 'aos'
import 'aos/dist/aos.css'
import 'aos/dist/aos.js'
const App = () => {
    useEffect(() =>{
        Aos.init({duration:4000})
    })

    
    const props1 = useSpring({
        from:{
            opacity:0,
            marginTop:-500,
        },
        to:{
            opacity:1,
            marginTop:0
        }
    })
    const props2 = useSpring({
        from:{
            opacity:0,
            marginLeft:-500
        },
        to:{
            opacity:1,
            marginLeft:60
        },
       
    })
    const props3 = useSpring({
        from:{
            opaxity:0,
            width:'0%',
            number:1
        },
        to:{
            opacity:1,
            width:'70%',
            number:70
        },
       
    })
    const props4 = useTransition({
        from:{
            opacity:0
        },
        enter:{
            opacity:1
        },
        leave:{
            opacity:0
        },
        delay: 200,
      
    })
    return  (
                <div>
                    <div  class="progress" >
                        <animated.div style={props3} class="progress-bar progress-bar-striped">70%</animated.div>
                    </div>
                    <animated.div data-aos='fade-left' className='container p-3'>
                        <div style={{width:'300px',height:'400px',padding:'10px',borderRadius:'15px',backgroundColor:'lavender',wordBreak:'break-all'}}> Lorem ipsum dolor sit amet consectetur, adipisicing elit. Fugit, accusantium fuga magni quod cum neque ex? Possimus voluptate facilis dolores. Blanditiis, architecto. Ad quasi, dolorem repellendus rerum itaque debitis repudiandae. </div>
                    </animated.div>
                    <animated.div data-aos='fade-right'  className='container p-3'>
                        <div style={{width:'300px',height:'400px',padding:'10px',borderRadius:'15px',backgroundColor:'lavender',wordBreak:'break-all'}}> Lorem ipsum dolor sit amet consectetur, adipisicing elit. Fugit, accusantium fuga magni quod cum neque ex? Possimus voluptate facilis dolores. Blanditiis, architecto. Ad quasi, dolorem repellendus rerum itaque debitis repudiandae. </div>
                    </animated.div>
                    <animated.div data-aos='fade-bottom'  className='container p-3'>
                        <div style={{width:'300px',height:'400px',padding:'10px',borderRadius:'15px',backgroundColor:'lavender',wordBreak:'break-all'}}> Lorem ipsum dolor sit amet consectetur, adipisicing elit. Fugit, accusantium fuga magni quod cum neque ex? Possimus voluptate facilis dolores. Blanditiis, architecto. Ad quasi, dolorem repellendus rerum itaque debitis repudiandae. </div>
                    </animated.div>
                    <Transition
                    from={{opacity:0}}
                    enter={{opacity:1}}
                    leave ={{opacity:0}}

                    > 
                        <div style={{width:'300px',height:'400px',padding:'10px',borderRadius:'15px',backgroundColor:'lavender',wordBreak:'break-all'}}> Lorem ipsum dolor sit amet consectetur, adipisicing elit. Fugit, accusantium fuga magni quod cum neque ex? Possimus voluptate facilis dolores. Blanditiis, architecto. Ad quasi, dolorem repellendus rerum itaque debitis repudiandae. </div>
                    </Transition>
                       

      </div>
      
            );
  }
   
  export default App;
