import React, { Component } from 'react';
import axios from 'axios'
import cookie from 'react-cookies'
import Navbar from './compenents/Navbar'
import { Redirect } from 'react-router';
class Login extends Component {
    constructor(props)
    {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handlefield = this.handlefield.bind(this);
        this.data1 = this.data1.bind(this);
        this.clearform = this.clearform.bind(this);
        this.clearform = this.clearform.bind(this)
    }
    state = {
        username : null,
        password:undefined,
        success:'',
        login:false
    }
    data1(data){
        let csrf = cookie.load('csrftoken');
        console.log(data)
            return <div style={{color:'red'}}>
                        {this.state.success}
          </div>
    }
            
    empty = () =>{
        setTimeout(() => {
            this.setState({
                success:''
            })
        }, 9000);
    }
    clearform(){
        this.PostCreateForm.reset();
    }
    handleSubmit(event){
        event.preventDefault();
        let comp = this;
        var formData = new FormData()
        formData.append('username',this.state.username);
        formData.append('password',this.state.password);
        let csrf = cookie.load('csrftoken');
        
        axios({
            method:'POST',
            url:'/sadfasd/',
            headers:{
                'Content-Type':'application/json',
                'X-CSRFToken':csrf
            },
            data:formData,
            credentials:'include'
        })
        .then(response => {
            console.log(response);
            if(response.data=== 'True')
            {
                comp.setState({
                    login:true
                })
            }
            else{
                comp.setState({
                    success:response.data
                })
            }
            
        })
        .catch(errors => {
            comp.setState({
                success:"Something Went Wrong!"
            })
            console.log(errors)
        })
        console.log('Handle Submit');
        this.clearform()
    }
    handlefield(event)
    {
        event.preventDefault();
        this.setState({
            [event.target.name]:event.target.value,
        })
        
    }
    componentDidUpdate(){
        setTimeout(() => {
            axios({
                method:'POST',
                url:'/check_l/',
                headers:{
                    'Content-Type':'application/json',
                    'X-CSRFToken':csrf
                },
                credentials:'include'
            })
                .then(response =>{
                    this.setState({
                        login:response.data
                    })
                })
        }, 1000);
    }
    render() {
        console.log(this.state)
        let csrf = cookie.load('csrftoken');
        if(csrf !== null && csrf !== undefined){
            if(this.state.login)
            {
                return <Redirect to='/' />
            }
            else{
                return ( 
            
                    <div>
                        <Navbar /> <br/>
                     
                     <div>{this.data1()}{this.empty()}   </div>
                     <form onSubmit={this.handleSubmit} ref = {(el) => this.PostCreateForm = el} className="container">
                     <h2>Log in Page</h2><br/>
                         <label> Foydalanuvchi nomi:</label>
                              <input className='form-control' type="text" name="username" id="" placeholder='Sizning Foydalanuvchi nomingiz' onChange={this.handlefield} required = 'required' />
                         <br/>
                         <label>Parol:</label>
                              <input className='form-control' type="password" name="password" placeholder='Password' onChange={this.handlefield} required = 'required' />
                         <br/>
                              <br/>
                              
                         <button type='submit' className='btn btn-info'>Submit</button>
                         
                     </form>
                 </div> 
                  );
            }
              
             
        }
        else{
            return <Redirect to='/notfound'/>
        }

    }
}
 
export default Login ;
