import React, { Component } from 'react';
import Navbar from './compenents/Navbar'
import axios from 'axios'

class Detail extends Component {
    constructor(props){
        super(props);
        this.state = {
            slug:null,
            array:[]
        }
    }
    componentDidMount()
    {   
        
        let comp = this;
        console.log(this,'MOUnt')
        comp.setState({
            slug:null,
        })
        if(comp.props.match)
        {
           const slug = comp.props.match.params.slug
           comp.setState({
               slug:slug
           })
           let url = "/detail/" + slug;
           axios.get(url)
           .then(response=>{
               let res = response.json();
               comp.setState({
                    array:res
                }) 
                console.log(comp.state.array)
           })
        }
        else{
            console.log("There is not any slug")
        }
    }
    render() {
        return (
        <div>
            <Navbar />
            <div>{this.state.array.name}</div>
        </div>
        );
    }
}
 
export default Detail;
