import cookie from 'react-cookies'
import 'whatwg-fetch'
import React, { Component,useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import PostInline from './compenents/post'
import Contact from './compenents/contact'
import {BrowserRouter,Route,Redirect,Switch} from 'react-router-dom'

class List extends Component {
    state = {
        posts:[],
      }
      componentDidMount()
      {
        this.setState({
          posts:[],
        })
        fetch('http://127.0.0.1:8000/api1/')
        .then((response) => response.json())
        .then(booksList => {
         if(this.state.posts != booksList)
         {
          this.setState({ posts: booksList });
         }  
        });
      }
// componentDidUpdate(){
//   setTimeout(() => {
//     fetch('api1/')
//     .then((response) => response.json())
//     .then(booksList => {
//       console.log(booksList);
//         this.setState({ posts: booksList });
//     });
//   }, 1000);
// }
    render() { 
        return ( 
            <div>
                <nav class="navbar navbar-inverse">
  <div className="container-fluid">
    <div className="navbar-header">
      <button type="button" className="navbar-toggle" data-toggle="collapse" data-target="#myNavbar">
        <span className="icon-bar"></span>
        <span className="icon-bar"></span>
        <span className="icon-bar"></span>                        
      </button>
      <a className="navbar-brand" href="/">React Market</a>
    </div>
    <div className="collapse navbar-collapse" id="myNavbar">
      <ul className="nav navbar-nav">
        <li ><a href="/">Home</a></li>
        <li className="active"><a href="/product_list">Product List</a></li>
        <li ><a href="/contact">Contact</a></li>
      </ul>
      <ul className="nav navbar-nav navbar-right">
        <li><a href="#"><span className="glyphicon glyphicon-user"></span> Sign Up</a></li>
        <li><a href="#"><span className="glyphicon glyphicon-log-in"></span> Login</a></li>
      </ul>
    </div>
  </div>
</nav>
   <div className="container">
   <h3>Product List </h3><br/>
       <div className='container'><div className='row'>{this.state.posts.map(post => <PostInline key = {post.id} post = {post} /> )}</div></div> 

   </div>
            </div>
         );
    }
}
 
export default List;
