import React, { Component } from 'react';
import cookie from 'react-cookies'
import axios from 'axios'
import { Redirect } from 'react-router';
class Form_checkout extends Component {
    constructor(props){
        super(props);
        this.handlesubmit = this.handlesubmit.bind(this);
        this.handleform = this.handleform.bind(this)
    }
    state = {
        posts:[],
        success:'',
        cart:0,
        wishlist:0,
        isloading:false,
        checkout:false,
        address:null,
        tel_num:null,
        name:null,
        surname:null,
        disabled:false,
        link:false
    }
    handlesubmit(event){
        event.preventDefault();
        console.log('Working')
        var formData = new FormData()
        let csrf = cookie.load('csrftoken')
        formData.append('address',this.state.address);
        formData.append('tel_num',this.state.tel_num);
        formData.append('name',this.state.name);
        formData.append('surname',this.state.surname);
        this.setState({
            disabled:true
        })
        axios({
            method:'POST',
            url:'http://127.0.0.1:8000/sefll123123lqmq2334412/',
            headers:{
                'content-type':'application/json',
                'X-CSRFToken': csrf
            },
            data:formData,
            credentials:'include'
            
        })
        .then(response =>{
            
            console.log(response)
            this.setState({
                success:"You booked successfully!!",
                disabled:false,
                link:true
            })
            setTimeout(() => {
                this.setState({
                    success:""
                })
            }, 3000);
        })
        .catch(errors =>{
            console.log(errors)
            this.setState({
                success:"Something went wrong!",
                disabled:false
            })
            setTimeout(() => {
                this.setState({
                    success:""
                })
            }, 3000);
        })
        console.log(this)
    }
    handleform(event){
        event.preventDefault()
        this.setState({
            [event.target.name]:event.target.value
        })
    }
    render() { 
        if(this.state.link){
            return <Redirect to='/cart/' />
        }
        else{
            return (
                <div className='container' >
                    <h1>Checkout</h1>
                    {this.state.success !== '' ?   <center><div className='alert alert-success fixed-top'>{this.state.success}</div></center> : ''}
                    <form onSubmit={this.handlesubmit}>
        
                        <label htmlFor="name">Your Name:</label>
                        <input type="text"  id='name' name='name' placeholder='Your Name' className='form-control' onChange = {this.handleform} required="required" />
                        <label htmlFor="surname">Your Surname:</label>
                        <input type="text"  id='surname' name='surname' placeholder='Your Surname' className='form-control' onChange = {this.handleform} required="required" />
                        <label htmlFor="address">Your Address:</label>
                        <input type="text" required='required' id='address' name='address' placeholder='Address where Product is delivered' className='form-control' required="required" onChange = {this.handleform} />
                        <label htmlFor="tel_num">Your Telephone Number:</label>
                        <input type="number" required='required' className='form-control' id='tel_num' name="tel_num" placeholder='Your telephone number to connect with you' required="required" onChange = {this.handleform} />
                        <br/>
                        {this.state.disabled ? <input type="submit" disabled  value="Submit" className='btn btn-info'/> : <input type="submit" value="Submit" className='btn btn-info'/>} 
                        <br/><br/>
                    </form>
        
                </div>
                );
        }
    }
}
 
export default Form_checkout;
